import React, { useEffect, useState } from 'react';

const MyInfoPage = () => {
  const [userInfo, setUserInfo] = useState({});
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  useEffect(() => {
    // 서버의 /authcheck 엔드포인트를 호출하여 현재 로그인 상태를 확인합니다.
    fetch('http://localhost:3001/authcheck')
      .then((response) => response.json())
      .then((data) => {
        if (data.isLogin) {
          // 로그인 상태인 경우, 사용자 정보를 가져오고 화면에 표시합니다.
          setIsLoggedIn(true);
          fetchUserInfo();
        } else {
          // 로그인되지 않은 경우, 로그인 버튼을 표시합니다.
          setIsLoggedIn(false);
        }
      })
      .catch((error) => {
        console.error('서버에서 로그인 상태를 확인하는 동안 오류 발생:', error);
      });
  }, []);

  const fetchUserInfo = () => {
    // 서버의 /myinfo 엔드포인트를 호출하고 사용자 정보를 가져옵니다.
    fetch('http://localhost:3001/myinfo')
      .then((response) => response.json())
      .then((data) => {
        if (data.error) {
          console.log(data.error);
        } else {
          // 가져온 사용자 정보를 상태 변수에 설정합니다.
          setUserInfo(data);
        }
      })
      .catch((error) => {
        console.error('서버에서 정보를 가져오는 동안 오류 발생:', error);
      });
  };

  const [showLogoutPopup, setShowLogoutPopup] = useState(false);
  const [showWithdrawPopup, setShowWithdrawPopup] = useState(false);

  const handleLogout = () => {
    if (window.confirm("정말로 로그아웃 하시겠습니까?")) {
      // 로그아웃 처리
      alert("로그아웃 되었습니다.");
    }
  };

  const handleWithdraw = () => {
    if (window.confirm("정말로 회원탈퇴 하시겠습니까?")) {
      // 회원탈퇴 처리
      alert("회원탈퇴 되었습니다.");
    }
  };

  const confirmLogout = () => {
    alert("로그아웃 되었습니다.");
    setShowLogoutPopup(false);
  };

  const confirmWithdraw = () => {
    alert("회원탈퇴 되었습니다.");
    setShowWithdrawPopup(false);
  };

  // useEffect(() => {
  //   updateUserInfo();
  // }, []); // 컴포넌트가 마운트될 때 한 번 실행

  // const updateUserInfo = () => {
  //   // API 호출이나 localStorage에서 사용자 정보 가져오기 등의 작업 수행
  //   // 여기서는 초기 상태를 사용합니다.
  //   setUserInfo({
  //     name: "홍길동",
  //     username: "hong123",
  //     password: "********",
  //     birthdate: "1990-01-01",
  //     email: "hong123@example.com",
  //     phone: "010-1234-5678",
  //   });
  // };

  return (
    <div>
      <style>
        {`
          body {
            margin: 0;
            padding: 20px;
        }
        
        h1 {
            text-align: center;
            margin-bottom: 20px;
        }
        
        table {
            margin-left: 10%;
            width: 80%;
            border-collapse: collapse;
        }
        
        th, td {
            padding: 10px;
            text-align: left;
        }
        
        th {
            background-color: #f2f2f2;
        }
        
        .button_container {
            text-align: center;
            margin-top: 20px;
        }
        
        .button_container button {
            margin: 10px;
            padding: 8px 16px;
            font-size: 14px;
            border: none;
            border-radius: 4px;
            cursor: pointer;
        }
        
        .button_container button.logout {
            background-color: #ff6b6b;
            color: #fff;
        }
        
        .button_container button.withdraw {
            background-color: #4dabf7;
            color: #fff;
        }
        `}
      </style>
      <h1>내 정보</h1>
      <table>
        <thead>
          <tr>
            <th>항목</th>
            <th>내용</th>
          </tr>
        </thead>
        <tbody>
          <tr>
            <td>이름</td>
            <td id="name">{userInfo.name}</td>
          </tr>
          <tr>
            <td>아이디</td>
            <td id="username">{userInfo.username}</td>
          </tr>
          <tr>
            <td>비밀번호</td>
            <td id="password">{userInfo.password}</td>
          </tr>
          <tr>
            <td>생년월일</td>
            <td id="birthdate">{userInfo.birthdate}</td>
          </tr>
          <tr>
            <td>이메일</td>
            <td id="email">{userInfo.email}</td>
          </tr>
          <tr>
            <td>전화번호</td>
            <td id="phone">{userInfo.phone}</td>
          </tr>
        </tbody>
      </table>
      <div className="button_container">
        <button className="logout" onClick={handleLogout}>
          로그아웃
        </button>
        <button className="withdraw" onClick={handleWithdraw}>
          회원탈퇴
        </button>
      </div>

      {/* 로그아웃 확인 팝업 */}
      {showLogoutPopup && (
        <div className="popup">
          <div className="popup_content">
            <div className="popup_actions">
              <button onClick={confirmLogout}>확인</button>
              <button onClick={() => setShowLogoutPopup(false)}>취소</button>
            </div>
          </div>
        </div>
      )}

      {/* 회원탈퇴 확인 팝업 */}
      {showWithdrawPopup && (
        <div className="popup">
          <div className="popup_content">
            <div className="popup_actions">
              <button onClick={confirmWithdraw}>확인</button>
              <button onClick={() => setShowWithdrawPopup(false)}>취소</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default MyInfoPage;
