import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';

function PostDetail() {
  const { id } = useParams();
  const [post, setPost] = useState(null);

  useEffect(() => {
    // 게시글 내용을 불러오는 API 요청을 보냅니다.
    async function fetchPost() {
      try {
        const response = await fetch(`http://localhost:3001/post/${id}`);
        if (response.ok) {
          const data = await response.json();
          setPost(data);
        } else {
          console.error('게시글 데이터 불러오기 오류:', response.statusText);
        }
      } catch (error) {
        console.error('API 요청 오류:', error);
      }
    }

    fetchPost();
  }, [id]);

  if (!post) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <style>
        {`
        .post_detail {
          text-align: center;
          padding: 20px;
        }

        .post_title {
          font-size: 24px;
          font-weight: bold;
        }

        .post_date {
          font-size: 16px;
        }

        .post_views {
          font-size: 16px;
          cursor: pointer;
        }
        `}
      </style>
      <div className="post_detail">
        <div className="post_title">{post.title}</div>
        <div className="post_date">{new Date(post.date).toLocaleDateString()}</div>
        <div
          className="post_views"
          onClick={() => {
            // 여기에 조회수를 증가시키는 로직을 추가합니다.
            alert('조회수 증가');
          }}
        >
          조회수: {post.views}
        </div>
        <p>{post.content}</p>
      </div>
    </div>
  );
}

export default PostDetail;
