import React, { useState, useEffect } from "react";

const items = [
  {
    name: "햄버거",
    src: require("./img/burger.jpg")
  },
  {
    name: "볶음밥",
    src: require("./img/fried-rice.jpg")
  },
  {
    name: "파스타",
    src: require("./img/pasta.jpg")
  },
  {
    name: "라면",
    src: require("./img/ramen.jpg")
  }
];

const FoodWorldCup = () => {
  const [foods, setFoods] = useState([]);
  const [displays, setDisplays] = useState([]);
  const [winners, setWinners] = useState([]);
  useEffect(() => {
    items.sort(() => Math.random() - 0.5);
    setFoods(items);
    setDisplays([items[0], items[1]]);
  }, []);

  const clickHandler = food => () => {
    if (foods.length <= 2) {
      if (winners.length === 0) {
        setDisplays([food]);
      } else {
        let updatedFood = [...winners, food];
        setFoods(updatedFood);
        setDisplays([updatedFood[0], updatedFood[1]]);
        setWinners([]);
      }
    } else if (foods.length > 2) {
      setWinners([...winners, food]);
      setDisplays([foods[2], foods[3]]);
      setFoods(foods.slice(2));
    }
  };
  return (
    <div>
      <style>
      {`
        .title {
          position: absolute;
          z-index: 2;
          top: 0;
          left: 50%;
          transform: translateX(-50%);
          background-color: #fff;
          padding: 0px 30px;
          text-transform: uppercase;
          padding-bottom: 10px;
        }
        .flex-1 {
          display: flex;
          flex: 1;
          overflow: hidden;
          background-color: black;
          position: relative;
          flex-direction: row; /* 왼쪽에서 오른쪽으로 배치 */
          justify-content: space-between; /* 왼쪽과 오른쪽 사이 간격을 최대화 */
          align-items: center; /* 세로 중앙 정렬 */
        }
        
        .food-img {
          width: 50%; /* 이미지의 너비를 50%로 조절 */
          height: auto; /* 높이를 자동으로 조절하여 비율 유지 */
          transition: 0.5s;
          cursor: pointer;
        }
        .food-img:hover {
          transform: scale(1.1);
          opacity: 0.8;
        }
        .name {
          position: absolute;
          z-index: 3;
          color: #fff;
          bottom: 10%;
          font-size: 90px;
          left: 50%;
          transform: translateX(-50%);
        `
      }
      </style>
      <h1 className="title">음식 월드컵</h1>
      {displays.map(d => {
        return (
          <div className="flex-1" key={d.name} onClick={clickHandler(d)}>
  <div style={{ display: "flex", flexDirection: "column" }}>
    <img className="food-img" src={d.src} />
    <div className="name">{d.name}</div>
  </div>
</div>

        );
      })}
    </div>
  );
};

export default FoodWorldCup;
