import React, { useState } from 'react';

function shuffleArray(array) {
  const shuffledArray = [...array];
  for (let i = shuffledArray.length - 1; i > 0; i--) {
    const j = Math.floor(Math.random() * (i + 1));
    [shuffledArray[i], shuffledArray[j]] = [shuffledArray[j], shuffledArray[i]];
  }
  return shuffledArray;
}

function FoodWorldCup() {
  const initialFoods = ['Pizza', 'Burger', 'Sushi', 'Taco', 'Pasta', 'Salad', 'Steak', 'Ice Cream'];

  const [tournament, setTournament] = useState([{ foods: initialFoods }]);

  const handleSelectWinner = (winners) => {
    if (winners.length === 1) {
      alert(`The winner is ${winners[0]}!`);
    } else {
      const shuffledWinners = shuffleArray(winners);
      const nextRound = [];
      for (let i = 0; i < shuffledWinners.length; i += 2) {
        nextRound.push(shuffledWinners[i]);
      }
      setTournament([...tournament, { foods: nextRound }]);
    }
  };

  return (
    <div className="App">
      <h1>Food Tournament</h1>
      {tournament.map((round, index) => (
        <div key={index}>
          <h2>Round {index + 1}</h2>
          {round.foods.map((food, foodIndex) => (
            <div key={foodIndex} className="match">
              {food}
            </div>
          ))}
          {round.foods.length > 1 && (
            <button onClick={() => handleSelectWinner(round.foods)}>Next Round</button>
          )}
        </div>
      ))}
      {tournament.length >= 3 && (
        <div>
          <h2>Winner</h2>
          <div className="match">
            {tournament[tournament.length - 1].foods[0]}
          </div>
        </div>
      )}
    </div>
  );
}

export default FoodWorldCup;



