import React, { useState } from 'react';
import { useParams } from 'react-router-dom';

function MyInfoPage() {
  const { userid } = useParams();
  
  const [userInfo, setUserInfo] = useState({
    name: "홍길동",
    username: cdflavor1113,
    password: "********",
    birthdate: "1990-01-01",
    email: "hong123@example.com",
    phone: "010-1234-5678"
  });

  // // 사용자 정보 업데이트 함수
  // const updateUserInfo = () => {
  //   // userInfo 상태 업데이트를 통해 UI가 자동으로 업데이트됩니다.
  // }

  // 로그아웃 함수
  const logout = () => {
    // 로그아웃 확인 팝업
    const confirmLogout = window.confirm("정말로 로그아웃 하시겠습니까?");
    if (confirmLogout) {
      // 로그아웃 처리
      alert("로그아웃 되었습니다.");
    }
  }

  // 회원탈퇴 함수
  const withdraw = () => {
    // 회원탈퇴 확인 팝업
    const confirmWithdraw = window.confirm("정말로 회원탈퇴 하시겠습니까?");
    if (confirmWithdraw) {
      // 회원탈퇴 처리
      alert("회원탈퇴 되었습니다.");
    }
  }

  return (
    <div>
      <h1>내 정보</h1>
      <table>
        <thead>
          <tr>
            <th>항목</th>
            <th>내용</th>
          </tr>
        </thead>
        <tbody>
          <tr>
            <td>이름</td>
            <td id="name">{userInfo.name}</td>
          </tr>
          <tr>
            <td>아이디</td>
            <td id="username">{userInfo.username}</td>
          </tr>
          <tr>
            <td>비밀번호</td>
            <td id="password">{userInfo.password}</td>
          </tr>
          <tr>
            <td>생년월일</td>
            <td id="birthdate">{userInfo.birthdate}</td>
          </tr>
          <tr>
            <td>이메일</td>
            <td id="email">{userInfo.email}</td>
          </tr>
          <tr>
            <td>전화번호</td>
            <td id="phone">{userInfo.phone}</td>
          </tr>
        </tbody>
      </table>
      <div className="button-container">
        <button className="logout" onClick={logout}>로그아웃</button>
        <button className="withdraw" onClick={withdraw}>회원탈퇴</button>
      </div>
    </div>
  );
}

export default MyInfoPage;
