import React, { useState } from 'react';

function shuffleArray(array) {
  const shuffledArray = [...array];
  for (let i = shuffledArray.length - 1; i > 0; i--) {
    const j = Math.floor(Math.random() * (i + 1));
    [shuffledArray[i], shuffledArray[j]] = [shuffledArray[j], shuffledArray[i]];
  }
  return shuffledArray;
}

const Tournament = ({ values }) => {
  const [currentRound, setCurrentRound] = useState(shuffleArray(values));
  const [winner, setWinner] = useState(null);
  const [selectedValues, setSelectedValues] = useState([]);

  const selectWinner = () => {
    const nextRound = [];
    const newSelectedValues = [];

    for (let i = 0; i < currentRound.length; i += 2) {
      const index1 = i;
      const index2 = i + 1;

      const value1 = currentRound[index1];
      const value2 = currentRound[index2];

      const winner = Math.random() < 0.5 ? value1 : value2;
      nextRound.push(winner);
      newSelectedValues.push(winner);
    }

    setSelectedValues(newSelectedValues);
    setCurrentRound(nextRound);

    if (nextRound.length === 1) {
      setWinner(nextRound[0]);
    }
  };

  return (
    <div>
      {winner ? (
        <div>
          <p>최종 우승자: {winner}</p>
        </div>
      ) : (
        <div>
          <p>현재 라운드: {currentRound.join(', ')}</p>
          <p>비교 중: {currentRound[0]} vs {currentRound[1]}</p>
          <button onClick={selectWinner}>다음 라운드 진행</button>
        </div>
      )}
    </div>
  );
};

function FoodWorldCup() {
  const initialValues = ["치킨", "피자", "햄버거", "삼겹살", "족발보쌈", "국수", "초밥", "마라탕"];
  return (
    <div className="App">
      <Tournament values={initialValues} />
    </div>
  );
}

export default FoodWorldCup;

