import React, { useEffect, useState } from "react";
import { Link } from 'react-router-dom';

const LoginPage = (props) => {
  const [id, setId] = useState("");
  const [password, setPassword] = useState("");

  const handleLoginSubmit = (event) => {
    validateForm(event);

    // 유효성 검사를 통과했을 때만 로그인 요청 보내기
    const userId = document.getElementById('user_id').value;
    const userPw = document.getElementById('user_pw').value;

    // 여기에서 서버로 로그인 요청 보내는 코드를 추가
    fetch('/login', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        userId: userId,
        userPassword: userPw,
      }),
    })
    .then((response) => response.json())
    .then((data) => {
      if (data.isSuccess) {
        alert('로그인 성공!');
        // 다른 로직을 추가하세요, 예를 들어 페이지 이동 등.
      } else {
        alert('로그인 실패: ' + data.message);
      }
    })
    .catch((error) => {
      console.error('로그인 요청 중 오류 발생:', error);
    });
  };
  
  useEffect(() => {
    const script = document.createElement('script');
    script.src = 'https://developers.kakao.com/sdk/js/kakao.js';
    script.onload = () => {
      window.Kakao.init('0560729fbda53d06035a69b6f812c484');
    };
    document.head.appendChild(script);
  
    // 컴포넌트가 언마운트될 때 실행될 함수 (cleanup 함수)
    return () => {
      // 이곳에서 정리(clean-up) 작업을 수행합니다.
      document.head.removeChild(script);
    };
  }, []);

  function validateForm(event) {
    if (event) {
      event.preventDefault();
    }

    const userId = document.getElementById('user_id').value;
    const userPw = document.getElementById('user_pw').value;
    const userIdError = document.getElementById('user_id_error');
    const userPwError = document.getElementById('user_pw_error');

    // 아이디 유효성 검사
    if (userId === '') {
        userIdError.innerText = '아이디를 입력해주세요.';
        userIdError.style.display = 'block';
    } else {
        userIdError.style.display = 'none';
    }

    // 비밀번호 유효성 검사
    if (userPw === '') {
        userPwError.innerText = '비밀번호를 입력해주세요.';
        userPwError.style.display = 'block';
    } else {
        userPwError.style.display = 'none';
    }
  }
  
  // 아이디 입력 시 에러 메시지를 확인하여 숨김 처리하는 함수
  function hideIdError() {
    const userId = document.getElementById('user_id').value;
    const userIdError = document.getElementById('user_id_error');

    if (userId !== '') {
    userIdError.style.display = 'none';
    }
  }

  // 비밀번호 입력 시 에러 메시지를 확인하여 숨김 처리하는 함수
  function hidePasswordError() {
    const userPw = document.getElementById('user_pw').value;
    const userPwError = document.getElementById('user_pw_error');

    if (userPw !== '') {
        userPwError.style.display = 'none';
    }
  }

  // Caps Lock이 켜져있는 경우
  function checkCapsLock(event) {
    const capsLockWarning = document.getElementById('caps_lock_warning');
    const capsLockEnabled = event.getModifierState && event.getModifierState('CapsLock');

    if (capsLockEnabled) {
      capsLockWarning.innerText = 'Caps Lock이 켜져있습니다.';
      capsLockWarning.style.display = 'block';
    } else {
      capsLockWarning.style.display = 'none';
    }
  }

  useEffect(() => {
    // 컴포넌트가 마운트되었을 때 이벤트 핸들러 등록
    document.getElementById('user_pw').addEventListener('keydown', checkCapsLock);
    document.getElementById('user_id').addEventListener('input', hideIdError);
    document.getElementById('user_pw').addEventListener('input', hidePasswordError);

    // 컴포넌트가 언마운트될 때 이벤트 핸들러 제거
    return () => {
      // 이벤트 핸들러가 등록되어 있는지 확인한 후 제거
      if (document.getElementById('user_pw')) {
        document.getElementById('user_pw').removeEventListener('keydown', checkCapsLock);
        document.getElementById('user_id').removeEventListener('input', hideIdError);
        document.getElementById('user_pw').removeEventListener('input', hidePasswordError);
      }
    };
  }, []);

  useEffect(() => {
    const loginSubmitButton = document.querySelector('.login_submit');
    if (loginSubmitButton) {
      loginSubmitButton.addEventListener('click', handleLoginSubmit);

      return () => {
        loginSubmitButton.removeEventListener('click', handleLoginSubmit);
      };
    }
  }, []);

  return (
    <html lang="ko">
      <head>
        <title> 로그인 </title>
        <meta charset="utf-8" />
        <meta http-equiv="X-UA-Compatible" content="IE=edge, chrome=1" />
        <style>
        {`
        @import url('https://fonts.googleapis.com/css?family=Raleway:400,700');

        * {
          box-sizing: border-box;
          margin: 0;
          padding: 0;
          font-family: Raleway, sans-serif;
        }
        
        .container {
          display: flex;
          align-items: center;
          justify-content: center;
          min-height: 100vh;
          background-color: #ffd9c6;
        }
        
        .screen {
          background: #ffffff;
          position: relative;
          width: 360px;
          height: 600px;
          border-radius: 5px;
          box-shadow: 0px 0px 24px #ffaebd;
        }
        
        /* 마우스 커서 인식하는 것 */
        .screen_content {
          z-index: 1;
          position: relative;
          height: 100%;
        }
        
        .flavor_logo {
          background-color: #ffffff;
          border: none;
          cursor: pointer;
          margin-top: 30px;
          margin-left: 26px;
        }
        
        .screen_background {
          position: absolute;
          top: 0;
          left: 0;
          right: 0;
          bottom: 0;
          z-index: 0;
          -webkit-clip-path: inset(0 0 0 0);
          clip-path: inset(0 0 0 0);
        }
        
        /* ID PW */
        .login {
          width: 360px;
          padding: 10px 42px 4px 42px;
        }
        
        .login_part {
          padding: 16px 0px;
          position: relative;
        }
        
        .login_input {
          border: none;
          border-bottom: 2px solid #D1D1D4;
          background: none;
          padding: 11px 11px 10px 15px;
          font-weight: 700;
          width: 100%;
          text-align: left;
        }
        
        .login_input:active,
        .login_input:focus,
        .login_input:hover {
          outline: none;
          border-bottom-color: #f9958c;
        }
        
        /* input[type="id"] {
          text-align: center;
        }
        
        input[type="password"] {
          text-align: center;
        } */
        
        .keep_check {
          position: relative;
          padding-left: 10px;
          display: flex;
          align-items: center;
        }
        
        .input_keep {
          position: relative;
          width: 17.5px; height: 17.5px;
          margin-left: 10px;
          border-radius: 50%;
          z-index: 2;
          border: 1.8px solid #9d9d9d;
          cursor: pointer;
          appearance: none;
          transform: translateY(3px);
        }
        
        /* 처음 상태 체크 표시 */
        .input_keep::before {
          position: relative;
          top: -3.2px; left: 3.2px;
          content: 'v';
          font-size: 16px;
          color: #9d9d9d;
        }
        
        .input_keep:checked::before {
          position: relative;
          color: #ffffff;
        }
        
        .input_keep:checked {
          position: relative;
          width: 17.5px; height: 17.5px;
          border-radius: 50%;
          border: 1.8px solid #ff96aa;
          background-color: #ff96aa;
          transition: 0.1s;
          z-index: 2;
        }
        
        /* 로그인 상태 유지 */
        .keep_text {
          display: inline-block;
          font-size: 13px;
          font-weight: 500;
          line-height:17px;
          color: #777777;
          position: relative;
          padding-left: 5px;
          z-index: 1;
          cursor: pointer;
        }
        
        .login_submit {
          background: #ffffff;
          font-size: 14px;
          margin-top: 30px;
          padding: 16px 0px 16px 116px ;
          border-radius: 26px;
          border: 1px solid #D4D3E8;
          text-transform: uppercase;
          font-weight: 700;
          display: flex;
          align-items: center;
          width: 100%;
          box-shadow: 0px 2px 2px #ff715b;
          cursor: pointer;
          transition: .2s;
        }
        
        .btn_login_submit {
          text-align: center;
        }
        
        .login_submit:active,
        .login_submit:focus,
        .login_submit:hover {
          border-color: #ff715b;
          outline: none;
        }
        
        .find_info {
          display: inline-flex; /* 가로로 정렬 */
          list-style-type: none;
          font-size: 10px;
          padding-left: 84px;
        }
        
        .find_idpw {
          margin-left: 13.9px;
          margin-right: 10px;
          color: #868686;
        }
        
        .sign_up {
          color: #868686;
        }
        
        .social_login {
          position: absolute;
          padding: 20px 0px 0px 40px;
        }
        
        .social_icons {
          display: flex;
          align-items: center;
          justify-content: center;
          padding-top: 20px;
        }
        
        .naver_icon, .kakao_icon, .google_icon {
          margin-right: 15px;
          background-color: #ffffff;
          border-color: #ffffff;
          cursor: pointer;
          border: none;
          box-shadow: none;
        }
        
        .facebook_icon {
          margin-right: 9.5px;
          background-color: #ffffff;
          border-color: #ffffff;
          cursor: pointer;
          border: none;
          box-shadow: none;
        }
        
        
        .naver_icon:active,
        .naver_icon:focus,
        .kakao_icon:active,
        .kakao_icon:focus,
        .facebook_icon:active,
        .facebook_icon:focus,
        .google_icon:active,
        .google_icon:focus {
          border: none;
          box-shadow: none;
        }
        
        .error_message {
          display: none;
          color: red;
          margin-top: -16px;
          margin-left: 5px;
          font-size: 12px;
        }`}
        </style>
      </head>
      <body>
        <div className="container">
          <div className="screen">
            <div className="screen_content">
              <Link to="/">
                <button type="flavor_logo" className="flavor_logo">
                <img
                  src="https://img1.daumcdn.net/thumb/R1280x0/?scode=mtistory2&fname=https%3A%2F%2Fblog.kakaocdn.net%2Fdn%2FNCkp7%2Fbtshz0iU8wZ%2FGdartd1kgTj0ZXMhJ0IKwk%2Fimg.png"
                  width="300" height="105" />
                </button>
              </Link>
              {/*  로그인 창 (아이디, 비밀번호, 로그인 유지 체크박스, 로그인 버튼)  */}
              <form className="login">
                <div className="login_part">
                  <input type="id" id="user_id" className="login_input" placeholder="아이디" onChange={event => {
                    setId(event.target.value);
                  }} />
                </div>
                <span className="error_message" id="user_id_error"></span>
                
                {/* 비밀번호 */}
                <div className="login_part">
                  <input type="password" id="user_pw" className="login_input" placeholder="비밀번호" onChange={event => {
                    setPassword(event.target.value);
                  }}/>
                </div>
                <span className="error_message" id="user_pw_error"></span>
                <span className="error_message" id="caps_lock_warning"></span>
                
                {/* 체크박스 (클릭 시 로그인 상태 유지) */}
                <label htmlFor="keep_check">
                  <input type="checkbox" id="keep" className="input_keep" value="off" />
                  <label for="keep" className="keep_text"> 로그인 상태 유지 </label>
                </label>
                <button id="login_button" className="btn login_submit">
                <span className="login_btn" onClick={() => {
                  <Link to="/"></Link>
                }}>로그인</span>
                </button>
              </form>
              {/* 로그인 창 (아이디, 비밀번호, 로그인 유지 체크박스, 로그인 버튼) 끝 */}

              {/* 아이디 & 비밀번호 찾기, 회원가입 글씨 버튼 */}
              <div className="find_info">
                <Link to="/findidpw" className="find_idpw"> 아이디 | 비밀번호 찾기 </Link>
                <Link to="/signup" className="sign_up" onClick={() => {
                  props.setMode("SIGNIN");
                  }}> 회원가입 </Link>

              </div>
              {/* 아이디 & 비밀번호 찾기, 회원가입 글씨 버튼 끝 */}

              {/* 간편 로그인 */}
              <div className="social_login">
                <h5> 간편 로그인 </h5>
                <div className="social_icons">
                  {/* 네이버 로그인 버튼 */}
                  <button type="button" className="naver_icon" id="naver_icon">
                    <a id="naverIdLogin_loginButton" href="javascript:void(0)">
                      <img src="https://clova-phinf.pstatic.net/MjAxODAzMjlfOTIg/MDAxNTIyMjg3MzM3OTAy.WkiZikYhauL1hnpLWmCUBJvKjr6xnkmzP99rZPFXVwgg.mNH66A47eL0Mf8G34mPlwBFKP0nZBf2ZJn5D4Rvs8Vwg.PNG/image.png" width="60" height="60" />
                    </a>
                  </button>

                  {/* 카카오 로그인 버튼 */}
                  <button type="button" className="kakao_icon" id="kakao_icon">
                    <img src="https://cdn.icon-icons.com/icons2/2429/PNG/512/kakaotalk_logo_icon_147272.png" width="61" height="61" />
                  </button>

                  {/* 페이스북 로그인 버튼 */}
                  <button type="button" className="facebook_icon" id="facebook_icon">
                    <a href="https://ko-kr.facebook.com/">
                      <img src="https://blog.kakaocdn.net/dn/UGS0q/btree5Viurw/l07AH1VgWJHm4stsAHLdL0/img.png" width="59.5" height="59.5" />
                    </a>
                  </button>

                  {/* 구글 로그인 버튼 */}
                  <button type="button" className="google_icon" id="google_icon">
                    <a href="https://www.google.co.kr/">
                      <img src="https://cdn1.iconfinder.com/data/icons/google-new-logos-1/32/google_chrome_new_logo-512.png" width="71.6" height="71.6" />
                    </a>
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>
      </body>
    </html>
  );
}

export default LoginPage;
