import React, { useState, useEffect } from 'react';

const SignUpPage = (props) => {
  const [isIdChecked, setIsIdChecked] = useState(false);

  const [id, setId] = useState("");
  const [password, setPassword] = useState("");
  const [password2, setPassword2] = useState("");

  // 아이디 중복 체크 함수
  const checkDuplicate = async () => {
    const userId = document.getElementById('user_id').value;

    // 아이디 길이 확인
    if (userId.length >= 6 && userId.length <= 20) {
      try {
        const response = await fetch('/checkDuplicate', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify({ userId })
          });

          if (response.ok) {
            const data = await response.json();
            if (data.isDuplicate) {
              // 중복된 아이디인 경우 오류 메시지 표시
              document.getElementById('su_id_error').innerText = '이미 사용 중인 아이디입니다.';
              document.getElementById('su_id_error').style.color = 'red';
              document.getElementById('su_id_error').style.display = 'block';
              setIsIdChecked(false); // 중복 체크 여부를 false로 설정
            } else {
              // 중복되지 않은 아이디인 경우 오류 메시지 숨김
              document.getElementById('su_id_error').innerText = '';
              document.getElementById('su_id_error').style.display = 'none';
              setIsIdChecked(true); // 중복 체크 여부를 true로 설정
            }
          } else {
            // 서버 오류 처리
            console.error('서버 응답 오류:', response.status, response.statusText);
          }
      } catch (error) {
        // 네트워크 오류 처리
        console.error('네트워크 오류:', error);
      }
    } else {
      // 아이디 길이 오류 메시지 표시
      document.getElementById('su_id_error').innerText = '6~20자의 영문 소문자와 숫자만 사용 가능합니다.';
      document.getElementById('su_id_error').style.color = 'red';
      document.getElementById('su_id_error').style.display = 'block';
      setIsIdChecked(false); // 중복 체크 여부를 false로 설정
    }
    };

    // 회원 가입 처리 함수
    const signUp = (userName, userId, userPw, userEmail, userNumber) => {
    // 회원 가입 로직 작성

    // 회원 가입 성공 시 결과 출력
    const isSuccess = true;
      if (isSuccess) {
        alert('회원 가입이 완료되었습니다.');
      } else {
        alert('회원 가입에 실패했습니다. 다시 시도해주세요.');
      }
    };

    // 기존에 존재하는 아이디 목록을 가져오는 함수 (임의로 구현)
    const idckExist = (userId) => {
      const existingUserIds = ['user01', 'user02', 'user03']; // 기존에 존재하는 아이디 목록
      return existingUserIds.includes(userId); // 아이디가 기존 목록에 포함되어 있는지 확인하고 결과를 반환합니다.
    };

    // 생년월일 선택 시 호출되는 함수
    const handleSelectChange = () => {
      const signUpBirthError = document.getElementById('su_birth_error');
      signUpBirthError.style.display = 'none';
    };

    // 이메일 도메인 선택 시 호출되는 함수
    const handleDomainOptionChange = (event) => {
      const selectedOption = event.target.value;
      const emailDomainTxt = document.getElementById('email_domain_txt');

      if (selectedOption === 'type') {
        emailDomainTxt.value = '';
        emailDomainTxt.disabled = false;
        emailDomainTxt.focus();
      } else {
        emailDomainTxt.value = selectedOption;
        emailDomainTxt.disabled = true;
      }
    };

    // 입력 필드의 keypress 이벤트 처리 함수
    const handleIdInputChange = (event) => {
      const input = event.target.value;
      const regex = /^[a-z0-9]+$/; // 영문 소문자와 숫자만 허용하는 정규식
      const errorMessage = document.getElementById('su_id_error');

      if (!regex.test(input)) {
        event.target.value = input.replace(/[^a-z0-9]/g, '');
        errorMessage.innerText = '영문 소문자와 숫자만 입력할 수 있습니다.';
        errorMessage.style.color = 'red';
        errorMessage.style.display = 'block';
      } else {
        errorMessage.style.display = 'none';
      }
    };

    // 이메일 입력 시 호출되는 함수
    const handleEmailInputChange = (event) => {
      const input = event.target.value;
      const regex = /^[a-zA-Z0-9!@#$%^&*()\-=+_~`{\[\\}|:;"'<,>.?/]*$/; // 영문, 숫자, 특수 기호를 제외한 문자 입력 방지 정규식
      const errorMessage = document.getElementById('su_email_error');

      if (!regex.test(input)) {
        event.target.value = input.replace(/[^a-zA-Z0-9!@#$%^&*()\-=+_~`{\[\\}|:;"'<,>.?/]/g, '');
        errorMessage.innerText = '영문, 숫자, 특수 기호만 입력할 수 있습니다.';
        errorMessage.style.color = 'red';
        errorMessage.style.display = 'block';
      } else {
        errorMessage.style.display = 'none';
      }
    };

    // 전화번호 입력 시 호출되는 함수
    const handleNumberInputChange = (event) => {
      const phoneNumber = event.target.value;
      const formattedNumber = formatPhoneNumber(phoneNumber);
      document.getElementById('user_number').value = formattedNumber;
    };

    // 전화번호 포맷팅 함수
    const formatPhoneNumber = (phoneNumber) => {
      phoneNumber = phoneNumber.replace(/[^0-9]/g, ''); // 입력된 문자열에서 숫자 이외의 문자 제거
      let formattedNumber = '';
      if (phoneNumber.length < 4) {
        formattedNumber = phoneNumber;
      } else if (phoneNumber.length < 7) {
        formattedNumber = phoneNumber.substr(0, 3) + '-' + phoneNumber.substr(3);
      } else if (phoneNumber.length < 11) {
        formattedNumber = phoneNumber.substr(0, 3) + '-' + phoneNumber.substr(3, 3) + '-' + phoneNumber.substr(6);
      } else {
        formattedNumber = phoneNumber.substr(0, 3) + '-' + phoneNumber.substr(3, 4) + '-' + phoneNumber.substr(7, 4);
      }
      return formattedNumber;
    };

    // Caps Lock 상태 확인 함수
    const checkCapsLock = (event, warningId) => {
      const capsLockWarning = document.getElementById(warningId);
      const capsLockEnabled = event.getModifierState && event.getModifierState('CapsLock');

      if (capsLockEnabled) {
        capsLockWarning.innerText = 'Caps Lock이 켜져있습니다.';
        capsLockWarning.style.display = 'block';
      } else {
        capsLockWarning.style.display = 'none';
      }
    };

    return (
    <>
    <div className="member">
        <b> 이름 * </b>
        <div className="field_name">
          <input type="text" id="user_name" placeholder="이름 입력" onKeyUp={validateName} />
        </div>
        <span className="error_message" id="su_name_error"></span>

        <b> 아이디 *</b>
          <div className="field_id">
            <input type="text" id="user_id" placeholder="아이디 입력 (6~20자)" onInput={handleIdInputChange} onChange={event => {
          setId(event.target.value);
        }} />
            <button id="idck_btn" onClick={checkDuplicate}> 중복 확인 </button>
        </div>
        <span className="error_message" id="su_id_error"></span>

        <b> 비밀번호  *</b>
          <div className="field_password">
            <input
              className="userpw" type="password" id="user_pw" placeholder="비밀번호 입력 (숫자, 특수문자 포함 8~20자)" onKeyUp={validatePassword} onKeyDown={(event) => checkCapsLock(event, 'caps_lock_warning1')} onBlur={() => (document.getElementById('caps_lock_warning1').style.display = 'none')} onChange={event => {
                setPassword(event.target.value);
              }} />
          </div>
        <span className="error_message" id="su_pw_error"></span>
        <span className="error_message" id="caps_lock_warning1"></span>

        <b>비밀번호 확인 *</b>
        <div className="field_password_check">
          <input
              className="userpwck" type="password" id="user_pwck" placeholder="비밀번호 재입력" onKeyUp={validatePasswordConfirmation} onKeyDown={(event) => checkCapsLock(event, 'caps_lock_warning2')} onBlur={() => (document.getElementById('caps_lock_warning2').style.display = 'none')} onChange={event => {
                  setPassword2(event.target.value);
                }} />
        </div>
        <span className="error_message" id="su_pwck_error"></span>
        <span className="error_message" id="caps_lock_warning2"></span>

          <b>생년월일</b>
          <div className="field_birth">
              <div className="field_birth_year">
                  <select id="birth_year" onChange={handleSelectChange}>
                  <option disabled selected>출생년도</option>
                  <option value="1950"> 1950년</option>
                  <option value="1951"> 1951년</option>
                  <option value="1952"> 1952년</option>
                  <option value="1953"> 1953년</option>
                  <option value="1954"> 1954년</option>
                  <option value="1955"> 1955년</option>
                  <option value="1956"> 1956년</option>
                  <option value="1957"> 1957년</option>
                  <option value="1958"> 1958년</option>
                  <option value="1959"> 1959년</option>
                  <option value="1960"> 1960년</option>
                  <option value="1961"> 1961년</option>
                  <option value="1962"> 1962년</option>
                  <option value="1963"> 1963년</option>
                  <option value="1964"> 1964년</option>
                  <option value="1965"> 1965년</option>
                  <option value="1966"> 1966년</option>
                  <option value="1967"> 1967년</option>
                  <option value="1968"> 1968년</option>
                  <option value="1969"> 1969년</option>
                  <option value="1970"> 1970년</option>
                  <option value="1971"> 1971년</option>
                  <option value="1972"> 1972년</option>
                  <option value="1973"> 1973년</option>
                  <option value="1974"> 1974년</option>
                  <option value="1975"> 1975년</option>
                  <option value="1976"> 1976년</option>
                  <option value="1977"> 1977년</option>
                  <option value="1978"> 1978년</option>
                  <option value="1979"> 1979년</option>
                  <option value="1980"> 1980년</option>
                  <option value="1981"> 1981년</option>
                  <option value="1982"> 1982년</option>
                  <option value="1983"> 1983년</option>
                  <option value="1984"> 1984년</option>
                  <option value="1985"> 1985년</option>
                  <option value="1986"> 1986년</option>
                  <option value="1987"> 1987년</option>
                  <option value="1988"> 1988년</option>
                  <option value="1989"> 1989년</option>
                  <option value="1990"> 1990년</option>
                  <option value="1991"> 1991년</option>
                  <option value="1992"> 1992년</option>
                  <option value="1993"> 1993년</option>
                  <option value="1994"> 1994년</option>
                  <option value="1995"> 1995년</option>
                  <option value="1996"> 1996년</option>
                  <option value="1997"> 1997년</option>
                  <option value="1998"> 1998년</option>
                  <option value="1999"> 1999년</option>
                  <option value="2000"> 2000년</option>
                  <option value="2001"> 2001년</option>
                  <option value="2002"> 2002년</option>
                  <option value="2003"> 2003년</option>
                  <option value="2004"> 2004년</option>
                  <option value="2005"> 2005년</option>
                  <option value="2006"> 2006년</option>
                  <option value="2007"> 2007년</option>
                  <option value="2008"> 2008년</option>
                  <option value="2009"> 2009년</option>
                  <option value="2010"> 2010년</option>
                  <option value="2011"> 2011년</option>
                  <option value="2012"> 2012년</option>
                  <option value="2013"> 2013년</option>
                  <option value="2014"> 2014년</option>
                  <option value="2015"> 2015년</option>
                  <option value="2016"> 2016년</option>
                  <option value="2017"> 2017년</option>
                  <option value="2018"> 2018년</option>
                  <option value="2019"> 2019년</option>
                  <option value="2020"> 2020년</option>
                  <option value="2021"> 2021년</option>
                  <option value="2022"> 2022년</option>
                  <option value="2023"> 2023년</option>
                  </select>
              </div>

              <div className="field_birth_month">
                  <select id="birth_month" onChange={handleSelectChange}>
                  <option disabled selected>월</option>
                  <option value="1">1월</option>
                  <option value="2">2월</option>
                  <option value="3">3월</option>
                  <option value="4">4월</option>
                  <option value="5">5월</option>
                  <option value="6">6월</option>
                  <option value="7">7월</option>
                  <option value="8">8월</option>
                  <option value="9">9월</option>
                  <option value="10">10월</option>
                  <option value="11">11월</option>
                  <option value="12">12월</option>
                  </select>
              </div>

              <div className="field_birth_day">
                  <select id="birth_day" onChange={handleSelectChange}>
                  <option disabled selected>일</option>
                  <option value="1">1일</option>
                  <option value="2">2일</option>
                  <option value="3">3일</option>
                  <option value="4">4일</option>
                  <option value="5">5일</option>
                  <option value="6">6일</option>
                  <option value="7">7일</option>
                  <option value="8">8일</option>
                  <option value="9">9일</option>
                  <option value="10">10일</option>
                  <option value="11">11일</option>
                  <option value="12">12일</option>
                  <option value="13">13일</option>
                  <option value="14">14일</option>
                  <option value="15">15일</option>
                  <option value="16">16일</option>
                  <option value="17">17일</option>
                  <option value="18">18일</option>
                  <option value="19">19일</option>
                  <option value="20">20일</option>
                  <option value="21">21일</option>
                  <option value="22">22일</option>
                  <option value="23">23일</option>
                  <option value="24">24일</option>
                  <option value="25">25일</option>
                  <option value="26">26일</option>
                  <option value="27">27일</option>
                  <option value="28">28일</option>
                  <option value="29">29일</option>
                  <option value="30">30일</option>
                  <option value="31">31일</option>
                  </select>
              </div>
              <span className="error_message" id="su_birth_error"></span>
          </div>

          <b>이메일 *</b>
          <div className="field_email">
              <input type="text" id="user_email" placeholder="이메일 주소" onInput={handleEmailInputChange} /> @
              <input className="field_email_domain_txt" id="email_domain_txt" type="text" disabled onInput={handleEmailInputChange} />
              <select className="field_email_domain_option" id="email_domain_option" onChange={handleDomainOptionChange}>
                  <option disabled selected>선택</option>
                  <option value="type">직접 입력</option>
                  <option value="naver.com">naver.com</option>
                  <option value="google.com">google.com</option>
                  <option value="hanmail.net">hanmail.net</option>
                  <option value="nate.com">nate.com</option>
                  <option value="kakao.com">kakao.com</option>
                  <option value="korea.com">korea.com</option>
              </select>
          </div>
          <span className="error_message" id="su_email_error"></span>

          <b> 전화번호 </b>
          <div className="field_number">
              <div>
                  <input type="text" name="phone" id="user_number" placeholder="휴대폰 번호 입력"  onInput={handleNumberInputChange} />
              </div>
          </div>
          <span className="error_message" id="su_number_error"></span>

          <input type="submit" id="signup_button" value="가입" onClick={() => {
            const userData = {
              userId: id,
              userPassword: password,
              userPassword2: password2,
            };
            fetch("http://localhost:3001/signup", {
              method: "post",
              headers: {
                "content-type": "application/json",
              },
              body: JSON.stringify(userData),
            })
              .then((res) => res.json())
              .then((json) => {
                if (json.isSuccess === true) {
                  alert('회원가입이 완료되었습니다!');
                } else {
                  alert('회원가입이 실패했습니다. 이유: ' + json.errorMessage);
                }
              })
              .catch((error) => {
                console.error("Fetch 오류:", error);
              });
          }} />

          <input type="button" id="signup_cancel_button" value="취소" />
      </div>
    </>
    );
};

export default SignUpPage;
