import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';

const RestaurantDetail = ({ selectedRestaurant }) => {
  const { id } = useParams();
  const [restaurantInfo, setRestaurantInfo] = useState(null);
  const navigate = useNavigate();
  const [rating, setRating] = useState(0);
  const [isFavorite, setIsFavorite] = useState(false);

  useEffect(() => {
    fetch(`http://localhost:3001/restaurant/${id}`)
      .then((response) => response.json())
      .then((data1) => {
        setRestaurantInfo(data1);
        console.log(data1);
      })
      .catch((error) => {
        console.error('음식점 정보를 가져오는 중 오류 발생:', error);
      });
  }, [id]);

  useEffect(() => {
    if (selectedRestaurant) {
      setRestaurantInfo(selectedRestaurant);
    }
  }, [selectedRestaurant]);

  // 별점을 설정하는 함수
  const handleRatingChange = (newRating) => {
    setRating(newRating);
  };

  // 서버로 별점을 보내는 함수
  const saveRatingToServer = () => {
    fetch(`http://localhost:3001/saveRating/${id}`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ rating }),
    })
      .then((response) => {
        if (response.ok) {
          console.log('별점이 성공적으로 저장되었습니다.');
        } else {
          console.error('별점 저장 중 오류 발생:', response.status);
        }
      })
      .catch((error) => {
        console.error('별점 저장 중 오류 발생:', error);
      });
  };

  // "찜하기" 버튼을 토글하는 함수
  const toggleFavorite = () => {
    setIsFavorite(!isFavorite);
  };

  return (
    <div>
      <style>
        {`
          .list_button {
            background-color: #ff6480;
            color: #fff;
            padding: 10px 15px;
            border: none;
            border-radius: 5px;
            cursor: pointer;
            margin: 10px 0px 0px 2%; // 상 우 하 좌
          }

          .list_button:hover {
            background-color: #c24e63;
          }

          .restaurant_detail {
            padding: 20px;
            border: 1px solid #ccc;
            border-radius: 8px;
            background-color: #f7f7f7;
            max-width: 400px;
            margin: 0 auto;
            box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
          }

          .star_container {
            display: flex;
            align-items: center;
          }

          .star {
            font-size: 24px;
            cursor: pointer;
            color: #ccc;
            margin-right: 5px;
          }

          .filled {
            color: #f7db45;
          }

          .heart_button {
            background: none;
            border: none;
            cursor: pointer;
            font-size: 24px;
            color: #ccc;
            position: absolute;
            top: -10px;
            right: 0px;
          }

          .heart_button.filled {
            color: #dc2a2d;
          }

          .save_button {
            background-color: #ff6480;
            color: #fff;
            padding: 10px 15px;
            border: none;
            border-radius: 5px;
            cursor: pointer;
            margin: 10px 0px 0px 2%; // 상 우 하 좌
          }

          .save_button:hover {
            background-color: #c24e63;
          }
        `}
      </style>
      <button className="list_button" onClick={() => navigate('/mapfoodtype')}>지도 보기</button>
      <div  className="restaurant_detail">
        {restaurantInfo ? (
          <div>
            <h2>{restaurantInfo.title}</h2>
            <p>전화번호: {restaurantInfo.phone_number}</p>
            <p>대표 메뉴: {restaurantInfo.menu}</p>
            <p>종류: {restaurantInfo.type}</p>
            <p>주소: ❗ ❗ ❗ 주소 적어야함 ❗ ❗ ❗</p>
            <p>별점: {rating}</p>

             {/* "찜하기" 버튼 */}
             <button
              className={`heart_button ${isFavorite ? 'filled' : ''}`}
              onClick={() => toggleFavorite()}
            >
              ♥  
              {/* 테두리 있는 거 {isFavorite ? '♥' : '♡'} */}
            </button>

            {/* 별점 UI */}
            <div className="star_container">
              {[1, 2, 3, 4, 5].map((star) => (
                <span
                  key={star}
                  className={`star ${star <= rating ? 'filled' : ''}`}
                  onClick={() => handleRatingChange(star)}
                >
                  {star <= rating ? '★' : '☆'}  
                  {/* 테두리 없는 거 ★ */}
                </span>
              ))}
            </div>

            <button className="save_button" onClick={() => saveRatingToServer()}>
              별점 저장
            </button>
          </div>
        ) : (
          <p>음식점 정보 로딩 중...</p>
        )}
      </div>
    </div>
  );
};

export default RestaurantDetail;
