import React, { useState } from 'react';

function shuffleArray(array) {
  const shuffledArray = [...array];
  for (let i = shuffledArray.length - 1; i > 0; i--) {
    const j = Math.floor(Math.random() * (i + 1));
    [shuffledArray[i], shuffledArray[j]] = [shuffledArray[j], shuffledArray[i]];
  }
  return shuffledArray;
}

const Tournament = ({ values }) => {
  const [foodList, setFoodList] = useState(shuffleArray(values));
  const [winner, setWinner] = useState(null);
  const [currentNode, setCurrentNode] = useState(0);

  const selectFoodList = () => {
    if (currentNode < foodList.length - 1) {
      setCurrentNode(currentNode + 1);
    } else {
      const nextRound = shuffleArray([]); // selectedValues 배열을 빈 배열로 업데이트
      setCurrentNode(0);
  
      if (nextRound.length === 1) {
        // Final winner
        setWinner(nextRound[0]);
      } else {
        setFoodList(nextRound);
      }
    }
  };

  // 랜덤으로 선택할 인덱스 계산
  const randomIndex = Math.floor(Math.random() * (foodList.length - 1));
  // 0번째 요소를 제외한 나머지 요소
  const otherFoods = foodList.filter((_, index) => index !== 0);
  // 랜덤으로 선택한 음식
  const randomFood = otherFoods[randomIndex];

  return (
    <div>
      {winner ? (
        <div>
          <p>최종 우승자: {winner}</p>
        </div>
      ) : (
        <div>
          <p>현재 라운드: {foodList.join(', ')}</p>
          <p>비교 중: {foodList[currentNode]} vs {randomFood}</p>
          <button onClick={selectFoodList}>선택</button>
        </div>
      )}
    </div>
  );
};

function FoodWorldCup() {
  const initialValues = ["치킨", "피자", "햄버거", "삼겹살", "족발보쌈", "국수", "초밥", "마라탕"];
  return (
    <div className="Food_world_cup">
      <Tournament values={initialValues} />
    </div>
  );
}

export default FoodWorldCup;


