import React, { useState } from 'react';

function FoodWorldCup() {
  const foodLists = shuffleArray(['Pizza', 'Burger', 'Sushi', 'Taco', 'Pasta', 'Salad', 'Steak', 'Ice Cream']);
  const [currentFoodLists, setCurrentFoodLists] = useState([{ foods: foodLists }]);
  const [showWinner, setShowWinner] = useState(false);
  const [winner, setWinner] = useState('');
  const [userSelectedWinner, setUserSelectedWinner] = useState(null); // 사용자가 선택한 우승자를 저장

  const handleSelectWinner = (winners) => {
    if (winners.length === 1) {
      setWinner(winners[0]);
      setShowWinner(true);
    } else {
      const shuffledWinners = shuffleArray(winners);
      const nextRound = [];
      for (let i = 0; i < shuffledWinners.length; i += 2) {
        nextRound.push(shuffledWinners[i]);
      }
      if (nextRound.length === 1) {
        setWinner(nextRound[0]);
        setShowWinner(true);
      }
      setCurrentFoodLists([...currentFoodLists, { foods: nextRound }]);
    }
  };

  const handleUserSelectWinner = (selectedWinner) => {
    setUserSelectedWinner(selectedWinner);
  };

  return (
    <div className="App">
      <h1>음식 월드컵</h1>
      {currentFoodLists.map((round, index) => (
        <div key={index}>
          <h2>{index + 1}라운드</h2>
          {round.foods.map((food, foodIndex) => (
            <div key={foodIndex} className="match">
              {food}
            </div>
          ))}
          {round.foods.length > 1 && (
            <div>
              <p>사용자 선택: {userSelectedWinner || "아직 선택하지 않음"}</p>
              {round.foods.length > 2 ? (
                <button onClick={() => handleSelectWinner(round.foods)}>다음 라운드</button>
              ) : (
                <button onClick={() => handleSelectWinner(round.foods)}>우승</button>
              )}
              <button onClick={() => handleUserSelectWinner(round.foods[0])}>첫 번째 음식 선택</button>
              <button onClick={() => handleUserSelectWinner(round.foods[1])}>두 번째 음식 선택</button>
            </div>
          )}
        </div>
      ))}

      {showWinner && !currentFoodLists[currentFoodLists.length - 1].foods.includes(winner) && (
        <div>
          <h2>최종 결과</h2>
          <div className="match">
            {winner}
          </div>
        </div>
      )}
    </div>
  );
}

export default FoodWorldCup;






