import React, { useState, useEffect } from 'react';

const options = ["치킨", "피자", "햄버거", "삼겹살", "족발보쌈", "국수", "초밥", "마라탕"];

function FoodWorldCup() {
  const [round, setRound] = useState(0);
  const [selectedOptions, setSelectedOptions] = useState([]);
  const [remainingOptions, setRemainingOptions] = useState([...options]);
  const [userSelections, setUserSelections] = useState([]);

  const handleOptionSelection = (option) => {
    if (userSelections.length < 4) {
      setUserSelections((prevSelections) => [...prevSelections, option]);
      setRemainingOptions((prevOptions) => prevOptions.filter((item) => item !== option));
    }

    if (userSelections.length === 4) {
      startNewRound();
    }
  };

  const startNewRound = () => {
    if (round < 3) {
      setRound((prevRound) => prevRound + 1);
      setSelectedOptions([]);
      setUserSelections([]);
      setRemainingOptions([...options]);
    }
  };

  useEffect(() => {
    if (round < 3) {
      const randomOptions = [];
      for (let i = 0; i < 2; i++) {
        const randomIndex = Math.floor(Math.random() * remainingOptions.length);
        randomOptions.push(remainingOptions[randomIndex]);
        setRemainingOptions((prevOptions) => prevOptions.filter((_, index) => index !== randomIndex));
      }
      setSelectedOptions(randomOptions);
    }
  }, [round, remainingOptions]);

  return (
    <div>
      <h2>라운드 {round + 1}</h2>
      {round < 3 ? (
        <div>
          <p>원하는 것을 선택하세요:</p>
          {selectedOptions.map((option, index) => (
            <button key={index} onClick={() => handleOptionSelection(option)}>{option}</button>
          ))}
          {userSelections.length > 0 && (
            <p>배열에 저장된 값은: {userSelections.join(", ")}</p>
          )}
        </div>
      ) : (
        <button onClick={startNewRound}>결과 확인</button>
      )}
    </div>
  );
}

export default FoodWorldCup;



