import React, { useState } from 'react';

const KeywordRecom = () => {
  const [selectedSex, setSelectedSex] = useState(null); // 초기에는 아무것도 선택되지 않음
  const [selectedAge, setSelectedAge] = useState(null); // 초기에는 아무것도 선택되지 않음

  const handleSexClick = (sex) => {
    setSelectedSex(sex); // 성별 선택 업데이트
    setSelectedAge(null); // 연령대 선택 초기화
  }

  const handleAgeClick = (age) => {
    setSelectedAge(age); // 연령대 선택 업데이트
    setSelectedSex(null); // 성별 선택 초기화
  }

  return (
    <div>
      <div className="keyword_list_sex">
        <button
          className={`keyword_button ${selectedSex === '남자' ? 'active' : ''}`}
          onClick={() => handleSexClick('남자')}
        >
          남자
        </button>
        <button
          className={`keyword_button ${selectedSex === '여자' ? 'active' : ''}`}
          onClick={() => handleSexClick('여자')}
        >
          여자
        </button>
      </div>
      <div className="keyword_list_age">
        <button
          className={`keyword_button ${selectedAge === '10대' ? 'active' : ''}`}
          onClick={() => handleAgeClick('10대')}
        >
          10대
        </button>
        <button
          className={`keyword_button ${selectedAge === '20대' ? 'active' : ''}`}
          onClick={() => handleAgeClick('20대')}
        >
          20대
        </button>
        <button
          className={`keyword_button ${selectedAge === '30대' ? 'active' : ''}`}
          onClick={() => handleAgeClick('30대')}
        >
          30대
        </button>
        <button
          className={`keyword_button ${selectedAge === '40대' ? 'active' : ''}`}
          onClick={() => handleAgeClick('40대')}
        >
          40대
        </button>
      </div>
    </div>
  );
}

export default KeywordRecom;
