import React, { useState } from 'react';

function FoodWorldCup() {
  const foods = [
    '피자',
    '햄버거',
    '스시',
    '파스타',
    '치킨',
    '라면',
    // 원하는 음식을 더 추가할 수 있습니다.
  ];

  const [matches, setMatches] = useState([]);
  const [currentMatchIndex, setCurrentMatchIndex] = useState(0);

  const generateRandomMatch = () => {
    const food1Index = Math.floor(Math.random() * foods.length);
    let food2Index;

    do {
      food2Index = Math.floor(Math.random() * foods.length);
    } while (food2Index === food1Index);

    const newMatch = {
      id: matches.length + 1,
      food1: foods[food1Index],
      food2: foods[food2Index],
      winner: null,
    };

    setMatches([...matches, newMatch]);
  };

  const handleMatchWinner = (winner) => {
    const updatedMatches = [...matches];
    updatedMatches[currentMatchIndex].winner = winner;
    setMatches(updatedMatches);
  };

  const handleNextMatch = () => {
    if (currentMatchIndex < matches.length - 1) {
      setCurrentMatchIndex(currentMatchIndex + 1);
    }
  };

  const currentMatch = matches[currentMatchIndex];

  return (
    <div className="App">
      <h1>토너먼트 음식 월드컵</h1>
      {currentMatch ? (
        <div className="match">
          <h2>매치 {currentMatch.id}</h2>
          <div className="food-options">
            <button onClick={() => handleMatchWinner(currentMatch.food1)}>
              {currentMatch.food1}
            </button>
            <span>vs</span>
            <button onClick={() => handleMatchWinner(currentMatch.food2)}>
              {currentMatch.food2}
            </button>
          </div>
        </div>
      ) : (
        <div>
          {currentMatchIndex >= matches.length ? (
            <p>모든 경기가 종료되었습니다!</p>
          ) : (
            <p>다음 라운드를 시작하려면 새 경기를 시작하세요.</p>
          )}
          {currentMatchIndex >= matches.length && (
            <button onClick={generateRandomMatch}>새 경기 시작</button>
          )}
          {currentMatchIndex < matches.length && (
            <button onClick={handleNextMatch}>다음 매치</button>
          )}
        </div>
      )}
    </div>
  );
}

export default FoodWorldCup;
