import React, { useEffect, useRef, useCallback } from 'react';

const RecomRandom = () => {
  const canvasRef = useRef(null);
  const lineRef = useRef(null);

  const product = [
    '떡볶이', '돈가스', '초밥', '피자', '냉면', '치킨', '족발', '피자', '삼겹살',
  ];

  const rotate = useCallback(() => {
    const $c = canvasRef.current;
    $c.style.transform = 'initial';
    $c.style.transition = 'initial';

    setTimeout(() => {
      const ran = Math.floor(Math.random() * product.length);

      const arc = 360 / product.length;
      const rotate = ran * arc + 3600 + arc * 3 - arc / 4;

      $c.style.transform = `rotate(-${rotate}deg)`;
      $c.style.transition = '2s';

      setTimeout(() => alert(`오늘의 야식은 ${product[ran]} 어떠신가요?`), 2000);
    }, 1);
  }, [product]);

  useEffect(() => {
    const $c = canvasRef.current;
    const ctx = $c.getContext('2d');

    const colors = [
      '#dc0936', '#e6471d', '#f7a416', '#efe61f', '#60b236',
      '#209b6c', '#169ed8', '#3f297e', '#87207b', '#be107f', '#e7167b',
    ];

    const newMake = () => {
      const [cw, ch] = [$c.width / 2, $c.height / 2];
      const arc = Math.PI / (product.length / 2);

      for (let i = 0; i < product.length; i++) {
        ctx.beginPath();
        ctx.fillStyle = colors[i % (colors.length - 1)];
        ctx.moveTo(cw, ch);
        ctx.arc(cw, ch, cw, arc * (i - 1), arc * i);
        ctx.fill();
        ctx.closePath();
      }
      
      ctx.fillStyle = '#fff';
      ctx.font = '18px Pretendard';
      ctx.textAlign = 'center';

      for (let i = 0; i < product.length; i++) {
        const angle = arc * i + arc / 2;

        ctx.save();

        ctx.translate(
          cw + Math.cos(angle) * (cw - 50),
          ch + Math.sin(angle) * (ch - 50),
        );

        ctx.rotate(angle + Math.PI / 2);

        product[i].split(' ').forEach((text, j) => {
          ctx.fillText(text, 0, 30 * j);
        });

        ctx.restore();
      }
    };

    newMake();

    // 고정된 선 그리기
    const lineCanvas = lineRef.current;
    const lineCtx = lineCanvas.getContext('2d');
    lineCtx.beginPath();
    lineCtx.strokeStyle = "#000"; // 선 색상
    lineCtx.lineWidth = 10; // 선 두께 늘리기
    lineCtx.lineCap = "round"; // 끝을 둥근 모양으로
    lineCtx.moveTo(0, 0); // 시작점
    lineCtx.lineTo(0, 50); // 끝점
    lineCtx.stroke();
    lineCtx.closePath();
  }, [product]);

  return (
    <div>
      <style>
        {`
        @import url("https://cdn.jsdelivr.net/gh/orioncactus/pretendard/dist/web/static/pretendard.css");

        * {
          font-family: Pretendard;
        }
        
        body {
          background: #f7f8fc;
        }
        
        canvas {
          transition: 2s;
        }
        
        button {
          background: #febf00;
          margin-top: 1rem;
          padding: .8rem 1.8rem;
          border: none;
          font-size: 1.5rem;
          font-weight: bold;
          border-radius: 5px;
          transition: .2s;
          cursor: pointer;
        }
        
        button:active {
          background: #444;
          color: #f9f9f9;
        }
          
        div {
          width: 380px;
          overflow: hidden;
          display: flex;
          align-items: center;
          flex-direction: column;
          position: relative;
        }
        
        .lineCanvas {
          position: absolute;
          top: -20px;
          left: 50%;
          transform: translateX(-50%);
        }
        `}
      </style>
      <canvas ref={canvasRef} width="380" height="380"></canvas>
      <canvas ref={lineRef} className="lineCanvas" width="10" height="60"></canvas>
      <button onClick={rotate}>룰렛 돌리기</button>
    </div>
  );
};

export default RecomRandom;
