import React, { useState, useEffect } from "react";

const items = [
  {
    name: "햄버거",
    src: require("./img/burger.jpg"),
  },
  {
    name: "볶음밥",
    src: require("./img/fried-rice.jpg"),
  },
  {
    name: "파스타",
    src: require("./img/pasta.jpg"),
  },
  {
    name: "라면",
    src: require("./img/ramen.jpg"),
  }
];


const FoodWorldCup = () => {
  const [foods, setFoods] = useState([]);
  const [displays, setDisplays] = useState([]);
  const [winners, setWinners] = useState([]);

  useEffect(() => {
    items.sort(() => Math.random() - 0.5);
    setFoods(items);
    setDisplays([items[0], items[1]]);
  }, []);

  const clickHandler = (food) => () => {
    if (foods.length <= 2) {
      if (winners.length === 0) {
        setDisplays([food]);
      } else {
        let updatedFood = [...winners, food];
        setFoods(updatedFood);
        setDisplays([updatedFood[0], updatedFood[1]]);
        setWinners([]);
      }
    } else if (foods.length > 2) {
      setWinners([...winners, food]);
      setDisplays([foods[2], foods[3]]);
      setFoods(foods.slice(2));
    }
  };

  return (
    <div>
      <style>
        {`
        .title {
          position: absolute;
          z-index: 2;
          left: 50%;
          transform: translateX(-50%);
          background-color: #ffffff;
          padding: 0px 30px;
          padding-bottom: 10px;
        }

        .food_list {
          top: 100px;
          display: flex;
          background-color: black;
        }

        .food-item {
          width: 50%;
          display: flex;
          flex-direction: column;
          align-items: center;
        }

        .food-img {
          width: 100%;
          max-height: 100%;
          transition: 0.5s;
          cursor: pointer;
        }

        .food-img:hover {
          transform: scale(1.005);
          opacity: 0.8;
        }

        .name {
          color: #fff;
          font-size: 90px;
        `}
      </style>
      <h1 className="title">음식 월드컵</h1>
      <div className="food_list">
        {displays.map((d) => (
          <div key={d.name} className="food-item" onClick={clickHandler(d)}>
            <img className="food-img" src={d.src} alt="food_world_cup"
              style={{ width: "500px", height: "500px" }} />

            <div className="name">{d.name}</div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default FoodWorldCup;
