export function validateForm(id, password, setIdError, setPasswordError) {
  // 아이디 유효성 검사
  if (id === '') {
    setIdError('아이디를 입력해주세요.');
  } else {
    setIdError(''); // 수정: 에러 메시지 초기화
  }

  // 비밀번호 유효성 검사
  if (password === '') {
    setPasswordError('비밀번호를 입력해주세요.');
  } else {
    setPasswordError(''); // 수정: 에러 메시지 초기화
  }
}

export function hideIdError(id, setIdError) {
  if (id !== '') {
    setIdError(''); // 수정: 에러 메시지 초기화
  }
}

export function hidePasswordError(password, setPasswordError) {
  if (password !== '') {
    setPasswordError(''); // 수정: 에러 메시지 초기화
  }
}

export function checkCapsLock(event, setCapsLockWarning) {
  const capsLockEnabled = event.getModifierState && event.getModifierState('CapsLock');

  if (capsLockEnabled) {
    setCapsLockWarning('Caps Lock이 켜져있습니다.');
  } else {
    setCapsLockWarning('');
  }
}
