import React, { useState, useEffect } from 'react';

const MyInfoHeart = () => {
  const [restaurantInfo, setRestaurantInfo] = useState(null);
  const [favorites, setFavorites] = useState([]);

  useEffect(() => {
    fetch('http://localhost:3001/myinfoheart', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({}), // 빈 요청
    })
      .then((response) => response.json())
      .then((data) => {
        if (data.length > 0) {
          // 기존의 찜 정보와 새로운 정보를 합쳐 저장
          setFavorites(prevFavorites => [...prevFavorites, ...data]);
        }
      })
      .catch((error) => {
        console.error('Error:', error);
      });
  }, []);

  useEffect(() => {
    if (favorites.length > 0) {
      const latestFavorite = favorites[favorites.length - 1];
      setRestaurantInfo({ title: `음식점 ID: ${latestFavorite.restaurantid}` });
    }
  }, [favorites]);

  return (
    <div>
      {restaurantInfo ? (
        <div>
          <h2>음식점 이름: {restaurantInfo.title}</h2>
          <p>찜한 횟수: {favorites.length}</p>
        </div>
      ) : (
        <p>찜 목록이 없습니다.</p>
      )}
    </div>
  );
};

export default MyInfoHeart;
