import React, { useState, useEffect } from 'react';

const MyInfoHeart = () => {
  const [restaurantInfo, setRestaurantInfo] = useState(null);
  const [isFavorite, setIsFavorite] = useState(false);

  useEffect(() => {
    fetch('http://localhost:3001/myinfoheart', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({}), // 빈 요청
    })
      .then((response) => response.json())
      .then((data) => {
        if (data.length > 0) {
          // 최신 찜 정보를 가져옴
          const latestFavorite = data[data.length - 1];
          setRestaurantInfo({ title: `Restaurant ID: ${latestFavorite.restaurantid}` });
          setIsFavorite(latestFavorite.heart === 1);
        }
      })
      .catch((error) => {
        console.error('Error:', error);
      });
  }, []);

  return (
    <div>
      {restaurantInfo ? (
        <div>
          <h2>음식점 이름: {restaurantInfo.title}</h2>
          <p>찜한 상태: {isFavorite ? '찜한 상태' : '찜하지 않은 상태'}</p>
        </div>
      ) : (
        <p>찜 목록이 없습니다.</p>
      )}
    </div>
  );
};

export default MyInfoHeart;
