import React, { useState } from 'react';

const KeywordRecom = () => {
  const [selectedSex, setSelectedSex] = useState(null);
  const [selectedAge, setSelectedAge] = useState(null);

  const handleSexClick = (sex) => {
    if (selectedSex === sex) {
      setSelectedSex(null); // 이미 선택된 성별 버튼을 다시 클릭하면 선택 해제
    } else {
      setSelectedSex(sex); // 새로운 성별 버튼을 클릭하면 선택
    }
  };

  const handleAgeClick = (age) => {
    if (selectedAge === age) {
      setSelectedAge(null); // 이미 선택된 연령대 버튼을 다시 클릭하면 선택 해제
    } else {
      setSelectedAge(age); // 새로운 연령대 버튼을 클릭하면 선택
    }
  };

  return (
    <div>
      <style> 
      {`
      .keyword_button {
        background-color: red; /* 기본 배경색 (클릭되지 않은 상태) */
        color: white;
        border: none;
        padding: 10px 20px;
        margin: 5px;
        cursor: pointer;
      }

      .keyword_button.active {
        background-color: blue; /* 클릭된 상태의 배경색 */
      }
      `}
      </style>
      <div className="keyword_list_sex">
        <button className={`keyword_button ${selectedSex === '남자' ? 'active' : ''}`}
          onClick={() => handleSexClick('남자')}> 남자 </button>
        <button className={`keyword_button ${selectedSex === '여자' ? 'active' : ''}`}
          onClick={() => handleSexClick('여자')}> 여자 </button>
      </div>
      <div className="keyword_list_menu">
        <button className={`keyword_button ${selectedAge === '한식' ? 'active' : ''}`}
          onClick={() => handleAgeClick('한식')}> 한식 </button>
        <button className={`keyword_button ${selectedAge === '중식' ? 'active' : ''}`}
          onClick={() => handleAgeClick('중식')}> 중식 </button>
        <button className={`keyword_button ${selectedAge === '일식' ? 'active' : ''}`}
          onClick={() => handleAgeClick('일식')}> 일식 </button>
        <button className={`keyword_button ${selectedAge === '양식' ? 'active' : ''}`}
          onClick={() => handleAgeClick('양식')}> 양식 </button>
        <button className={`keyword_button ${selectedAge === '패스트푸드' ? 'active' : ''}`}
          onClick={() => handleAgeClick('패스트푸드')}> 패스트푸드 </button>
        <button className={`keyword_button ${selectedAge === '분식' ? 'active' : ''}`}
          onClick={() => handleAgeClick('분식')}> 분식 </button>
      </div>
    </div>
  );
}

export default KeywordRecom;
