import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const KeywordRecom = () => {
  const [selectedSex, setSelectedSex] = useState(null);
  const [selectedAge, setSelectedAge] = useState(null);
  const [selectedType, setSelectedType] = useState([]); // 초기값을 빈 배열로 설정
  const [selectedMood, setSelectedMood] = useState([]);
  const [selectedKeywords, setSelectedKeywords] = useState(''); // 선택한 키워드를 저장할 상태

  const handleSexClick = (sex) => {
    if (selectedSex === sex) {
      setSelectedSex(null);
    } else {
      setSelectedSex(sex);
    }
  };

  const handleAgeClick = (age) => {
    if (selectedAge === age) {
      setSelectedAge(null);
    } else {
      setSelectedAge(age);
    }
  };

  const handleTypeClick = (type) => {
    // 복사본을 만들어 작업하기
    const newSelectedType = [...selectedType];

    // 이미 선택된 메뉴이면 선택 해제, 아니면 추가
    if (newSelectedType.includes(type)) {
      newSelectedType.splice(newSelectedType.indexOf(type), 1);
    } else {
      // 3개까지만 선택 가능하도록
      if (newSelectedType.length < 3) {
        newSelectedType.push(type);
      }
    }

    setSelectedType(newSelectedType);
  };

  const handleMoodClick = (mood) => {
    const newSelectedMood = [...selectedMood];

    if (newSelectedMood.includes(mood)) {
      newSelectedMood.splice(newSelectedMood.indexOf(mood), 1);
    } else {
      if (newSelectedMood.length < 3) {
        newSelectedMood.push(mood);
      }
    }

    setSelectedMood(newSelectedMood);
  };

  const handleCompleteClick = () => {
    // if (!selectedSex) {
    //   setSelectedKeywords('성별을 입력해주세요!');
    //   return;
    // }

    // if (!selectedAge) {
    //   setSelectedKeywords('나이대를 입력해주세요!');
    //   return;
    // }

    // if (!selectedType) {
    //   setSelectedKeywords('메뉴를 입력해주세요!');
    //   return;
    // }

    const keywords = [selectedSex, selectedAge, ...selectedType, selectedMood,].filter(Boolean);
    
    if (keywords.length === 0) {
      setSelectedKeywords('키워드를 입력해주세요!');
    } else {
      setSelectedKeywords('선택한 키워드는 \'' + keywords.join(', ') + '\' 입니다');
    }
  };

  return (
    <div>
      <style> 
      {`
        .flavor_logo {
          display: block;
          margin: 0 auto;
          margin-top: 20px;
          margin-bottom: 40px;
        }

        .keyword_list_box,
        .game_button {
          margin-left: 100px;
        }

        .keyword_button {
          background-color: gray;
          color: white;
          border: none;
          border-radius: 5px;
          padding: 10px 20px;
          margin: 5px;
          cursor: pointer;
          margin-bottom: 10px;
        }

        .keyword_button.active {
          background-color: black;
        }

        .submit_button,
        .foodworldcup_button,
        .roulette_button {
          background-color: #ff6480;
          color: white;
          border: none;
          border-radius: 5px;
          padding: 10px 20px;
          margin: 5px;
          cursor: pointer;
        }
      `}
      </style>
      <Link to="/">
        <button type="flavor_logo" className="flavor_logo">
          <img src="https://img1.daumcdn.net/thumb/R1280x0/?scode=mtistory2&fname=https%3A%2F%2Fblog.kakaocdn.net%2Fdn%2FNCkp7%2Fbtshz0iU8wZ%2FGdartd1kgTj0ZXMhJ0IKwk%2Fimg.png" width="300" height="105" alt="flavor_logo" />
        </button>
      </Link>
      <div className="keyword_list_box">
        <div className="keyword_list_sex">
          <button className={`keyword_button ${selectedSex === '남자' ? 'active' : ''}`}
            onClick={() => handleSexClick('남자')}> 남자 </button>
          <button className={`keyword_button ${selectedSex === '여자' ? 'active' : ''}`}
            onClick={() => handleSexClick('여자')}> 여자 </button>
        </div>
        <div className="keyword_list_age">
          <button className={`keyword_button ${selectedAge === '10대' ? 'active' : ''}`}
            onClick={() => handleAgeClick('10대')}> 10대 </button>
          <button className={`keyword_button ${selectedAge === '20대' ? 'active' : ''}`}
            onClick={() => handleAgeClick('20대')}> 20대 </button>
          <button className={`keyword_button ${selectedAge === '30대' ? 'active' : ''}`}
            onClick={() => handleAgeClick('30대')}> 30대 </button>
          <button className={`keyword_button ${selectedAge === '40대' ? 'active' : ''}`}
            onClick={() => handleAgeClick('40대')}> 40대 </button>
        </div>
        <div className="keyword_list_type">
          {['한식', '중식', '일식', '양식', '패스트푸드', '분식'].map((type) => (
            <button key={type} className={`keyword_button ${selectedType.includes(type) ? 'active' : ''}`}
              onClick={() => handleTypeClick(type)}> {type} </button>
          ))}
        </div>
        <div className="keyword_list_mood">
          {['#분위기 좋은', '#깨끗한', '#저렴한', '#넓은', '#혼밥하기 좋은', '#조용한', '#푸짐한'].map((mood) => (
            <button key={mood} className={`keyword_button ${selectedMood.includes(mood) ? 'active' : ''}`}
              onClick={() => handleMoodClick(mood)}> {mood} </button>
          ))}
        </div>
        <div>
          <button className="submit_button" onClick={handleCompleteClick}>완료</button>
          {selectedKeywords && <p>{selectedKeywords}</p>}
        </div>
      </div>
      <div className="game_button">
        <Link to="/foodworldcup">
          <button className="foodworldcup_button">음식 월드컵 하기</button>
        </Link>
        <Link to="/roulette">
          <button className="roulette_button">룰렛 돌리기</button>
        </Link>
      </div>
    </div>
  );
}

export default KeywordRecom;
