import React, { useState, useEffect } from 'react';

const MapFoodSH = () => {
  const [selectedRestaurant, setSelectedRestaurant] = useState(null);
  const [map, setMap] = useState(null);
  const [markers, setMarkers] = useState([]);
  const [selectedFoodType, setSelectedFoodType] = useState([]);
  const [previousInfowindow, setPreviousInfowindow] = useState(null);

  useEffect(() => {
    const script = document.createElement('script');
    script.src =
      'https://dapi.kakao.com/v2/maps/sdk.js?appkey=0560729fbda53d06035a69b6f812c484&libraries=services';
    script.async = true;
    script.onload = () => {
      // 스크립트가 로드된 후에 실행되는 부분
      const centerLat = 37.297764; // 중심 좌표의 위도
      const centerLng = 127.069340; // 중심 좌표의 경도

      // 카카오 지도 설정
      const mapOptions = {
        center: new window.kakao.maps.LatLng(centerLat, centerLng), // 중심 좌표로 설정
        level: 2, // 지도 확대 레벨
      };

      const map = new window.kakao.maps.Map(
        document.getElementById('map'),
        mapOptions
      );

      // 클릭 이벤트 리스너를 추가
      window.kakao.maps.event.addListener(map, 'click', function (mouseEvent) {
        // 클릭한 위도, 경도 정보를 가져옵니다
        const latlng = mouseEvent.latLng;

        const message =
          latlng.getLat().toFixed(6) + ', ' + latlng.getLng().toFixed(6);

        const resultDiv = document.getElementById('result');
        resultDiv.innerHTML = message;
      });

      fetch('http://localhost:3001/mapfoodsh', {
        method: 'post',
        headers: {
          'Content-Type': 'application.json',
        },
        credentials: 'include',
      })
        .then((response) => response.json())
        .then((data) => {
          if (data && data.length > 0) {
            const positions = data.map((item) => ({
              title: item.title,
              latlng: new window.kakao.maps.LatLng(item.latitude, item.longitude),
              type: item.type,
              number: item.phone_number,
              menu: item.menu,
            }));

            const markerList = positions.map((position) => {
              const marker = new window.kakao.maps.Marker({
                position: position.latlng,
                map: mapInstance,
              });
              const infowindow = new window.kakao.maps.InfoWindow({
                content: `<div style="padding:5px;">${position.title}</div>`,
                removable: true,
              });

              window.kakao.maps.event.addListener(marker, 'click', () => {
                // 이전 인포윈도우를 닫습니다
                if (previousInfowindow) {
                  previousInfowindow.close();
                }
              
                // 새 인포윈도우를 엽니다
                infowindow.open(mapInstance, marker);
                setSelectedRestaurant(position);
              
                // 현재 인포윈도우를 이전 인포윈도우로 설정합니다
                setPreviousInfowindow(infowindow);
              });

              return { ...position, marker, infowindow };
            });

            setMarkers(markerList);
          } else {
            console.log('데이터가 비어 있습니다.');
          }
        })
        .catch((error) => {
          console.error('데이터를 가져오는 중 오류 발생:', error);
        });
    };
    document.head.appendChild(script);
  }, []);

  const filterMarkersByFoodType = (foodType) => {
    setSelectedFoodType(foodType);

    markers.forEach((marker) => marker.marker.setMap(null));

    if (foodType === '전체') {
      markers.forEach((marker) => marker.marker.setMap(map));
    } else {
      markers
        .filter((marker) => marker.type === foodType || marker.type.includes(foodType))
        .forEach((marker) => marker.marker.setMap(map));
    }

    // 이전 인포 윈도우를 닫음
    if (previousInfowindow) {
      previousInfowindow.close();
    }
  };

  return (
    <div>
      <style>
      {`
        .map_container {
          width: 100%;
          height: 600px;
          margin: 0 auto;
          position: relative;
        }

        .food_type_buttons {
          display: flex;
          gap: 10px;
          margin: 10px 0px 10px 10px;
        }
        
        .food_type_buttons button {
          background-color: #ff6480;
          color: white;
          border: none;
          padding: 10px 20px;
          cursor: pointer;
          border-radius: 20px;
        }
        
        .food_type_buttons button:hover {
          background-color: #fd98ab;
          color: white;
        }

        .restaurant_info {
          margin-top: 10px;
          margin-left: 10px; /* 원하는 마진 값을 지정하세요. */
        }
      `}
      </style>
      <div className="food_type_buttons">
        <button onClick={() => filterMarkersByFoodType('전체')}>전체</button>
        <button onClick={() => filterMarkersByFoodType('한식')}>한식</button>
        <button onClick={() => filterMarkersByFoodType('중식')}>중식</button>
        <button onClick={() => filterMarkersByFoodType('일식')}>일식</button>
        <button onClick={() => filterMarkersByFoodType('양식')}>양식</button>
        <button onClick={() => filterMarkersByFoodType('패스트푸드')}>패스트푸드</button>
        <button onClick={() => filterMarkersByFoodType('분식')}>분식</button>
      </div>
      <div className="map_container" id="map"></div>
      {selectedRestaurant && (
        <div className="restaurant_info">
          <h3>{selectedRestaurant.title}</h3>
          <p> 전화번호: {selectedRestaurant.number}</p>
          <p> 대표메뉴: {selectedRestaurant.menu}</p>
        </div>
      )}
    </div>
  );
};

export default MapFoodSH;
