            .board_title {
                margin-bottom: 30px;
            }
            
            .board_title strong {
                font-size: 3rem;
            }
            
            .board_title p {
                margin-top: 5px;
                font-size: 1.4rem;
            }
            
            .bt_wrap {
                margin-top: 30px;
                text-align: center;
