            }
            
            .board_page a.num.on:hover {
                background: #b2b2b2;
            }
            
            .board_page a:first-child {
                border-left: 1px solid #ddd;
            }
            
            .board_view {
                width: 100%;
                border-top: 2px solid #000;
            }
            
            .board_view .title {
                padding: 20px 15px;
                border-bottom: 1px dashed #ddd;
