              <dt>제목</dt>
              {/* value 대신에 value 속성을 defaultValue로 변경합니다. */}
              <dd><input type="text" placeholder="제목 입력" defaultValue={title} /></dd>
            </dl>
          </div>
          <div className="info">
            <dl>
              <dt>이름</dt>
              {/* value 대신에 value 속성을 defaultValue로 변경합니다. */}
              <dd><input type="text" placeholder="글쓴이 입력" defaultValue={name} /></dd>
            </dl>
            <dl>
              <dt>비밀번호</dt>
              {/* value 대신에 value 속성을 defaultValue로 변경합니다. */}
              <dd><input type="password" placeholder="비밀번호 입력" defaultValue={password} /></dd>
            </dl>
          </div>
          <div className="cont">
            {/* textarea 내용은 value 속성 대신에 defaultValue로 변경합니다. */}
            <textarea placeholder="내용 입력" defaultValue={content} />
          </div>
        </div>
        <div className="bt_wrap">
          {/* 수정 버튼은 a 태그 대신 button 태그로 변경합니다. */}
          <Link to="/boardall" className="lar">확인</Link>
          <Link to="/boardview" className="lar">취소</Link>
        </div>
      </div>
    </div>
  );
}

export default BoardEdit;
