import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';

function BoardList() {
  const [posts, setPosts] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const postsPerPage = 5;
  const navigate = useNavigate();

  useEffect(() => {
    async function fetchPosts() {
      try {
        const response = await fetch('http://localhost:3001/boardlist');
        if (response.ok) {
          const data = await response.json();
          setPosts(data);
        } else {
          console.error('게시물 데이터 불러오기 오류:', response.statusText);
        }
      } catch (error) {
        console.error('API 요청 오류:', error);
      }
    }

    fetchPosts();
  }, []);

  const totalPosts = posts.length;
  const totalPages = Math.ceil(totalPosts / postsPerPage);
  const indexOfFirstPost = (currentPage - 1) * postsPerPage;
  const indexOfLastPost = indexOfFirstPost + postsPerPage;
  const sortedPosts = [...posts].reverse();
  const currentPosts = sortedPosts.slice(indexOfFirstPost, indexOfLastPost);

  const pageButtons = [];
  for (let page = 1; page <= totalPages; page++) {
    pageButtons.push(
      <button
        key={page}
        onClick={() => setCurrentPage(page)}
        disabled={page === currentPage}
      >
        {page}
      </button>
    );
  }

  return (
    <div className="board-container">
      <h1>게시판</h1>
      <ul className="post-list">
        {currentPosts.map((post, index) => (
          <li key={post.id}>
            <Link to={`/post/${post.id}`}>
              순서: {totalPosts - indexOfFirstPost - index} 제목: {post.title} 작성일: {post.date} 조회수: {post.views}
            </Link>
          </li>
        ))}
      </ul>
      <div className="page-navigation">
        <button onClick={() => setCurrentPage(1)} disabled={currentPage === 1}>
          처음
        </button>
        <button onClick={() => setCurrentPage(currentPage - 1)} disabled={currentPage === 1}>
          이전
        </button>
        {pageButtons}
        <button
          onClick={() => setCurrentPage(currentPage + 1)}
          disabled={currentPage === totalPages || totalPosts === 0}
        >
          다음
        </button>
        <button
          onClick={() => setCurrentPage(totalPages)}
          disabled={currentPage === totalPages || totalPosts === 0}
        >
          마지막
        </button>
      </div>
      <br />
      <button onClick={() => navigate('/createpostform')}>게시물 추가</button>
    </div>
  );
}

export default BoardList;
