import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';

const MyInfoHeart = () => {
  const userid = 'cdflavor1113';
  const location = useLocation();
  const { state } = location;
  const restaurantInfo = state && state.restaurantInfo;
  const isFavorite = state && state.isFavorite;

  const [favoriteRestaurants, setFavoriteRestaurants] = useState([]);

  useEffect(() => {
    if (restaurantInfo && isFavorite) {
      fetch('http://localhost:3001/myinfoheart', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ userid }),
      })
        .then((response) => response.json())
        .then((data) => {
          setFavoriteRestaurants(data);
        })
        .catch((error) => {
          console.error('Error:', error);
        });
    }
  }, [userid, restaurantInfo, isFavorite]);

  return (
    <div>
      {!restaurantInfo || typeof isFavorite === 'undefined' ? (
        <p>찜 목록이 없습니다.</p>
      ) : (
        <div>
          <h2>음식점 이름: {restaurantInfo.title}</h2>
          <p>찜한 상태: {isFavorite ? '찜한 상태' : '찜하지 않은 상태'}</p>
          {favoriteRestaurants.map((restaurant) => (
            <div key={restaurant.id}>
              {/* 각 즐겨찾는 음식점의 상세 정보 표시 */}
              <h3>{restaurant.title}</h3>
              {/* 다른 음식점 세부 정보 */}
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default MyInfoHeart;
