import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';

function PostDetail() {
  const { id } = useParams();
  const [post, setPost] = useState(null);

  useEffect(() => {
    // 여기에서 게시글 내용을 불러오는 API 요청을 보내고 디비에서 가져오는 로직을 구현합니다.
    // 이 부분에서 서버 API와의 통신이 필요하므로, fetch 또는 axios 등을 사용하여 API 요청을 보내야 합니다.
    // API 요청을 보내고 게시글 내용을 가져오는 코드를 작성합니다.
    
    // 예시: fetch를 사용한 API 요청
    fetch(`http://localhost:3001/post/${id}`)
      .then((response) => response.json())
      .then((data) => {
        setPost(data);
      })
      .catch((error) => {
        console.error('게시글 데이터 불러오기 오류:', error);
      });
  }, [id]);

  if (!post) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <h1>{post.title}</h1>
      <p>작성일: {post.date}</p>
      <p>조회수: {post.views}</p>
      <p>{post.content}</p>
    </div>
  );
}

export default PostDetail;
