import React, { useState, useEffect } from 'react';

const initFoodList = ["치킨", "피자", "햄버거", "삼겹살", "족발보쌈", "국수", "초밥", "마라탕"];

function shuffleArray(array) {
  const shuffledArray = array.slice(); // 복사본 생성
  for (let i = shuffledArray.length - 1; i > 0; i--) {
    const j = Math.floor(Math.random() * (i + 1));
    [shuffledArray[i], shuffledArray[j]] = [shuffledArray[j], shuffledArray[i]];
  }
  return shuffledArray;
}

function FoodWorldCup() {
  const [round, setRound] = useState(0); // 현재 라운드 번호
  const [matchup, setMatchup] = useState([]); // 현재 라운드의 경기 배열
  const [selectedFoods, setSelectedFoods] = useState([]); // 선택된 음식들의 배열

  useEffect(() => {
    // 초기 라운드 설정 (랜덤으로 섞기)
    const shuffledInitFoodList = shuffleArray(initFoodList);
    const initialMatchup = divideIntoPairs(shuffledInitFoodList);
    setMatchup(initialMatchup);
  }, []);


  // 배열을 2개씩 나누는 함수
  function divideIntoPairs(arr) {
    const pairs = [];
    for (let i = 0; i < arr.length; i += 2) {
      pairs.push([arr[i], arr[i + 1]]);
    }
    return pairs;
  }

  // 다음 라운드로 진행하는 함수
  function nextRound() {
    if (round < Math.log2(initFoodList.length)) {
      const winners = selectedFoods.slice();
      setMatchup(divideIntoPairs(winners));
      setSelectedFoods([]);
      setRound(round + 1);
    }
  }

  // 음식 선택 함수
  function selectFood(winner) {
    setSelectedFoods([...selectedFoods, winner]);
    if (selectedFoods.length === Math.pow(2, round)) {
      nextRound();
    }
  }

  return (
    <div>
      <h1>음식 월드컵</h1>
      <p>현재 라운드: {round + 1}</p>
      {matchup.map((pair, index) => (
        <div key={index}>
          <button onClick={() => selectFood(pair[0])}>{pair[0]}</button>
          <span> vs </span>
          <button onClick={() => selectFood(pair[1])}>{pair[1]}</button>
        </div>
      ))}
    </div>
  );
}

export default FoodWorldCup;

