import React, { useState } from 'react';

const options = ["Option 1", "Option 2", "Option 3", "Option 4", "Option 5", "Option 6", "Option 7", "Option 8"];

const FoodWorldCup = () => {
  const [round, setRound] = useState(1);
  const [matches, setMatches] = useState([]);
  const [selectedOption, setSelectedOption] = useState(null);
  const [isNextMatchEnabled, setIsNextMatchEnabled] = useState(false);

  const initializeMatches = () => {
    const initialMatches = [];
    for (let i = 0; i < options.length; i += 2) {
      initialMatches.push([i, i + 1]);
    }
    setMatches(initialMatches);
  };

  const startRound = () => {
    if (round <= 4) {
      initializeMatches();
    }
  };

  const handleOptionClick = (optionIndex) => {
    setSelectedOption(optionIndex);
    setIsNextMatchEnabled(true);
  };

  const handleNextMatch = () => {
    if (selectedOption !== null) {
      const updatedMatches = [...matches];
      const selectedMatchIndex = Math.floor(selectedOption / 2);
      updatedMatches[selectedMatchIndex].push(selectedOption);
      setMatches(updatedMatches);
      setSelectedOption(null);
      setIsNextMatchEnabled(false);
    }
  };

  const handleNextRound = () => {
    if (matches.length === 1) {
      setRound(round + 1);
    }
  };

  return (
    <div className="food-world-cup">
      {round <= 4 ? (
        <div>
          <h2>Round {round}</h2>
          <div className="match">
            {matches.length > 0 && matches[0].map((optionIndex) => (
              <button
                key={optionIndex}
                onClick={() => handleOptionClick(optionIndex)}
                disabled={selectedOption !== null}
              >
                {options[optionIndex]}
              </button>
            ))}
          </div>
          <button onClick={handleNextMatch} disabled={!isNextMatchEnabled}>
            Next Match
          </button>
          {round < 4 && (
            <button onClick={handleNextRound} disabled={matches.length !== 1}>
              Next Round
            </button>
          )}
        </div>
      ) : (
        <div className="result">
          <h2>Result</h2>
          <p>You selected: {options[matches[0][0]]}</p>
        </div>
      )}
      {round === 1 && (
        <button onClick={startRound}>Start Tournament</button>
      )}
    </div>
  );
};

export default FoodWorldCup;
