import React, { useEffect, useState } from 'react';
import { useNavigate , useParams } from 'react-router-dom';

const MyInfoPage = () => {
  const { userid } = useParams();
  const [userInfo, setUserInfo] = useState({});
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [showLogoutPopup, setShowLogoutPopup] = useState(false);
  const [showWithdrawPopup, setShowWithdrawPopup] = useState(false);

  const navigate = useNavigate();

  useEffect(() => {
    async function fetchAuthcheck() {
      try {
        const response = await fetch(`http://localhost:3001/authcheck?userid=${userid}`, {
          credentials: 'include',
        });
        console.log(userid);
        if (response.ok) {
          const data = await response.json();
          if (data.isLogin) {
            console.log(data.isLogin);
            setIsLoggedIn(true);
            fetchUserInfo();
          } else {
            setIsLoggedIn(false);
            navigate("/login");
          }
        } else {
          console.error('서버에서 로그인 상태를 확인하는 동안 오류 발생:', response.statusText);
        }
      } catch (error) {
        console.error('API 요청 오류:', error);
      }
    }
    
    async function fetchUserInfo() {
      try {
        const response = await fetch(`http://localhost:3001/myinfo?userid=${userid}`, {
          credentials: 'include',
        });
        if (response.ok) {
          const data = await response.json();
          if (!data.error) {
            setUserInfo(data);
  
            if ('name' in data) {
              console.log(data.name);
            } else {
              console.log('name 필드가 존재하지 않습니다.');
            }
          } else {
            console.log(data.error);
          }
        } else {
          console.error('사용자 정보 가져오기 오류:', response.statusText);
        }
      } catch (error) {
        console.error('API 요청 오류:', error);
      }
    }
  
    fetchAuthcheck();
  }, [userid]);

  const confirmLogout = () => {
    alert("로그아웃 되었습니다.");
    setShowLogoutPopup(false);
  };

  const confirmWithdraw = () => {
    alert("회원탈퇴 되었습니다.");
    setShowWithdrawPopup(false);
  };

  return (
    <div>
      <style>
        {`
          /* 스타일 코드 */
        `}
      </style>
      <h1>내 정보</h1>
      {isLoggedIn ? (
        <div>
          <table>
            <thead>
              <tr>
                <th>항목</th>
                <th>내용</th>
              </tr>
            </thead>
            <tbody>
              <tr>
                <td>이름</td>
                <td>{userInfo?.hasOwnProperty('name') ? userInfo.name : '사용자 이름이 없습니다'}</td>
              </tr>
              <tr>
                <td>아이디</td>
                <td>{userInfo?.hasOwnProperty('userid') ? userInfo.userid : '사용자 아이디가 없습니다'}</td>
              </tr>
              <tr>
                <td>비밀번호</td>
                <td>{userInfo?.hasOwnProperty('password') ? userInfo.password : '비밀번호가 없습니다'}</td>
              </tr>
              <tr>
                <td>생년월일</td>
                <td>{userInfo?.hasOwnProperty('birthdate') ? userInfo.birthdate : '생년월일이 없습니다'}</td>
              </tr>
              <tr>
                <td>이메일</td>
                <td>{userInfo?.hasOwnProperty('email') ? userInfo.email : '이메일이 없습니다'}</td>
              </tr>
              <tr>
                <td>전화번호</td>
                <td>{userInfo?.hasOwnProperty('number') ? userInfo.number : '전화번호가 없습니다'}</td>
              </tr>
            </tbody>
          </table>
          <div className="button_container">
            <button className="logout" 
            // onClick={handleLogout}
            >
              로그아웃
            </button>
            <button className="withdraw" 
            // onClick={handleWithdraw}
            >
              회원탈퇴
            </button>
          </div>
        </div>
      ) : (
        <div>
          <p>로그인이 필요합니다.</p>
          {/* 로그인 버튼 또는 로그인 페이지로 이동하는 링크 추가 */}
        </div>
      )}

      {showLogoutPopup && (
        <div className="popup">
          <div className="popup_content">
            <div className="popup_actions">
              <button onClick={confirmLogout}>확인</button>
              <button onClick={() => setShowLogoutPopup(false)}>취소</button>
            </div>
          </div>
        </div>
      )}

      {showWithdrawPopup && (
        <div className="popup">
          <div className="popup_content">
            <div className="popup_actions">
              <button onClick={confirmWithdraw}>확인</button>
              <button onClick={() => setShowWithdrawPopup(false)}>취소</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default MyInfoPage;
