import React, { useState } from 'react';

const options = ["치킨", "피자", "햄버거", "삼겹살", "족발보쌈", "국수", "초밥", "마라탕"];

const FoodWorldCup = () => {
  const [round, setRound] = useState(1);
  const [matches, setMatches] = useState(generateMatches(options));

  function generateMatches(players) {
    const newMatches = [];
    for (let i = 0; i < players.length; i += 2) {
      newMatches.push({ player1: players[i], player2: players[i + 1] });
    }
    return newMatches;
  }

  function handleMatchWinner(winner) {
    const updatedMatches = matches.map((match, index) => {
      if (index === round - 1) {
        return { ...match, winner };
      }
      return match;
    });

    setMatches(updatedMatches);

    if (updatedMatches.length === 1) {
      // 토너먼트 종료
      console.log("토너먼트 종료. 최종 승자: ", updatedMatches[0].winner);
    } else {
      setRound(round + 1);
    }
  }

  return (
    <div className="App">
      <h1>토너먼트 라운드 {round}</h1>
      {matches.map((match, index) => (
        <div key={index}>
          <p>{match.player1} vs. {match.player2}</p>
          {match.winner ? (
            <p>승자: {match.winner}</p>
          ) : (
            <button onClick={() => handleMatchWinner(match.player1)}>승자: {match.player1}</button>
          )}
        </div>
      ))}
    </div>
  );
}

export default FoodWorldCup;

