import React, { useState, useEffect } from "react";
import { Link } from 'react-router-dom';

const items = [
  {
    name: "햄버거",
    src: require("./img/burger.jpg"),
  },
  {
    name: "볶음밥",
    src: require("./img/fried-rice.jpg"),
  },
  {
    name: "파스타",
    src: require("./img/pasta.jpg"),
  },
  {
    name: "라면",
    src: require("./img/ramen.jpg"),
  }
];


const FoodWorldCup = () => {
  const [foods, setFoods] = useState([]);
  const [displays, setDisplays] = useState([]);
  const [winners, setWinners] = useState([]);

  useEffect(() => {
    items.sort(() => Math.random() - 0.5);
    setFoods(items);
    setDisplays([items[0], items[1]]);
  }, []);

  const clickHandler = (food) => () => {
    if (foods.length <= 2) {
      if (winners.length === 0) {
        setDisplays([food]);
      } else {
        let updatedFood = [...winners, food];
        setFoods(updatedFood);
        setDisplays([updatedFood[0], updatedFood[1]]);
        setWinners([]);
      }
    } else if (foods.length > 2) {
      setWinners([...winners, food]);
      setDisplays([foods[2], foods[3]]);
      setFoods(foods.slice(2));
    }
  };

  return (
    <div>
      <style>
        {`
        .title {
          position: absolute;
          left: 50%;
          background-color: #ffffff;
        }

        .flavor_logo {
          top: 10px;
          left: 10px;
        }

        .box {
          margin-top: 10px;
          top: 40px;
          display: flex;
          background-color: black;
        }

        .food_list {
          width: 50%;
          display: flex;
          flex-direction: column;
          align-items: center;
        }

        .food_img {
          width: 100%;
          margin-top: 70px;
          max-height: 100%;
          transition: 0.5s;
          cursor: pointer;
        }

        .food_img:hover {
          transform: scale(1.005);
          opacity: 0.8;
        }

        .name {
          color: #fff;
          font-size: 90px;
        `}
      </style>
      <Link to="/">
        <button type="flavor_logo" className="flavor_logo">
          <img src="https://img1.daumcdn.net/thumb/R1280x0/?scode=mtistory2&fname=https%3A%2F%2Fblog.kakaocdn.net%2Fdn%2FNCkp7%2Fbtshz0iU8wZ%2FGdartd1kgTj0ZXMhJ0IKwk%2Fimg.png" width="300" height="105" alt="flavor_logo" />
        </button>
      </Link>
      <h1 className="title">음식 월드컵</h1>
      <div className="box">
        {displays.map((d) => (
          <div key={d.name} className="food_list" onClick={clickHandler(d)}>
            <img className="food_img" src={d.src} alt="food_world_cup"
              style={{ width: "550px", height: "550px" }} />

            <div className="name">{d.name}</div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default FoodWorldCup;
