import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';

function BoardList({ handlePostClick }) {
  const [posts, setPosts] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const postsPerPage = 5;
  const navigate = useNavigate();

  useEffect(() => {
    async function fetchPosts() {
      try {
        const response = await fetch('http://localhost:3001/boardlist');
        if (response.ok) {
          const data = await response.json();
          setPosts(data);
        } else {
          console.error('게시물 데이터 불러오기 오류:', response.statusText);
        }
      } catch (error) {
        console.error('API 요청 오류:', error);
      }
    }

    fetchPosts();
  }, []);

  const totalPosts = posts.length;
  const totalPages = Math.ceil(totalPosts / postsPerPage);
  const indexOfFirstPost = (currentPage - 1) * postsPerPage;
  const indexOfLastPost = indexOfFirstPost + postsPerPage;
  const sortedPosts = [...posts].reverse();
  const currentPosts = sortedPosts.slice(indexOfFirstPost, indexOfLastPost);

  const pageButtons = [];
  for (let page = 1; page <= totalPages; page++) {
    pageButtons.push(
      <button
        key={page}
        onClick={() => setCurrentPage(page)}
        disabled={page === currentPage}
        className="nav_button"
      >
        {page}
      </button>
    );
  }

  return (
    <div>
      <style>
        {`
        /* BoardList.css */
        .board_container {
          display: flex;
          flex-direction: column;
          align-items: center;
          text-align: center;
          margin-top: 40px;
        }

        .post_list {
          list-style: none;
          padding: 0;
          margin-top: 10px;
        }

        .post_list_header {
          display: flex;
          justify-content: space-between;
          margin-bottom: 10px;
          font-weight: bold;
        }

        .post_list_item {
          display: flex;
          justify-content: space-between;
          margin-bottom: 10px;
        }

        .post_number,
        .post_title,
        .post_date,
        .post_views {
          width: 100px; /* 고정된 너비 설정 */
        }

        .post_date {
          transform: translateX(0%);
        }

        .nav_button {
          background-color: #007BFF;
          color: #fff;
          border: none;
          border-radius: 5px;
          padding: 5px 10px;
          margin: 0 5px;
          cursor: pointer;
          min-width: 40px;
          height: 30px;
        }

        .nav_button:disabled {
          background-color: #ccc;
          cursor: not-allowed;
        }

        .add_button {
          background-color: #28A745;
          color: #fff;
          border: none;
          border-radius: 5px;
          padding: 10px 20px;
          cursor: pointer;
          min-width: 40px;
          height: 30px;
        }

        .add_button:hover {
          background-color: #218838;
        }
        `}
      </style>
      <div className="board_container">
        <h1>게시판</h1>
        <ul className="post_list">
          <li className="post_list_header">
            <span className="post_number">순서</span>
            <span className="post_title">제목</span>
            <span className="post_date">작성일</span>
            <span className="post_views">조회수</span>
          </li>
          {currentPosts.map((post, index) => (
            <li className="post_list_item" key={post.id}>
              <span className="post_number">{totalPosts - indexOfFirstPost - index}</span>
              <Link to={`/post/${post.id}`} onClick={() => handlePostClick(post.id)}>
                {post.title}
              </Link>
              <span className="post_date">{new Date(post.date).toLocaleDateString()}</span>
              <span className="post_views">{post.views}</span>
            </li>
          ))}
        </ul>
        <div className="page_navigation">
          <button onClick={() => setCurrentPage(1)} disabled={currentPage === 1} className="nav_button">처음</button>
          <button onClick={() => setCurrentPage(currentPage - 1)} disabled={currentPage === 1} className="nav_button">이전</button>
          {pageButtons}
          <button onClick={() => setCurrentPage(currentPage + 1)} disabled={currentPage === totalPages || totalPosts === 0} className="nav_button">다음</button>
          <button onClick={() => setCurrentPage(totalPages)} disabled={currentPage === totalPages || totalPosts === 0} className="nav_button">마지막</button>
        </div>
        <br />
        <button onClick={() => navigate('/createpostform')} className="add_button">게시물 추가</button>
      </div>
    </div>
  );
}

export default BoardList;
