import React, { useState, useEffect } from 'react';

const MyInfoHeart = () => {
  const [restaurantInfo, setRestaurantInfo] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetch('http://localhost:3001/myinfoheart', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({}), // 빈 요청
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error('네트워크 오류');
        }
        return response.json();
      })
      .then((data) => {
        if (data.length > 0) {
          setRestaurantInfo(data);
        }
      })
      .catch((error) => {
        setError('데이터를 가져오는 중 오류가 발생했습니다');
        console.error('Error:', error);
      });
  }, []);

  return (
    <div>
      <style>
        {`
        .info_container {
          text-align: center;
          margin-top: 20px;
        }
        
        .restaurant_list {
          list-style: none;
          padding: 0;
        }
        
        .restaurant_item {
          background-color: #f7f7f7;
          border: 1px solid #ccc;
          border-radius: 8px;
          margin: 10px 0;
          padding: 10px;
          box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
        }
        
        .restaurant_item h2 {
          font-size: 1.5em;
          margin-bottom: 10px;
        }
        
        .total_favorites {
          margin-top: 20px;
          font-weight: bold;
        }        
        `}
      </style>
      <div className="info_container">
        {restaurantInfo.length > 0 ? (
          <div>
            <h2>나의 찜 목록</h2>
            <ul className="restaurant_list">
              {restaurantInfo.map((restaurant, index) => (
                <li className="restaurant_item" key={index}>
                  <h2>{restaurant.title}</h2>
                  <p>전화번호: {restaurant.phone_number}</p>
                  <p>메뉴: {restaurant.menu}</p>
                  <p>종류: {restaurant.type}</p>
                  <p>주소: {restaurant.address}</p>
                </li>
              ))}
            </ul>
            <p className="total_favorites">총 찜한 횟수: {restaurantInfo.length}</p>
          </div>
        ) : error ? (
          <p>{error}</p>
        ) : (
          <p>찜 목록이 없습니다.</p>
        )}
      </div>
    </div>
  );
};

export default MyInfoHeart;
