import React, { useState, useEffect } from 'react';

const initialValues = ["치킨", "피자", "햄버거", "삼겹살", "족발보쌈", "국수", "초밥", "마라탕"];

function FoodWorldCup() {
  const [round, setRound] = useState(1);
  const [foods, setFoods] = useState([...initialValues]);
  const [selectedFoods, setSelectedFoods] = useState([]);

  useEffect(() => {
    if (round > 1) {
      const newFoods = [];
      for (let i = 0; i < foods.length; i += 2) {
        const randomIndex = Math.floor(Math.random() * 2);
        newFoods.push(foods[i + randomIndex]);
      }
      setFoods(newFoods);
    }
  }, [round, foods]);

  const handleFoodClick = (food) => {
    setSelectedFoods([...selectedFoods, food]);

    if (selectedFoods.length === 2) {
      setRound(round + 1);
      setSelectedFoods([]);

      if (round === Math.log2(initialValues.length)) {
        // 마지막 라운드가 끝났을 때 우승자를 처리하거나 결과를 보여줄 수 있습니다.
        // 여기서는 간단하게 경기 결과를 출력합니다.
        console.log(`우승자: ${selectedFoods[0]}`);
      }
    }
  };

  return (
    <div className="App">
      <h1>Food World Cup - Round {round}</h1>
      <div className="matchup">
        {foods.map((food, index) => (
          <div key={index} className="food" onClick={() => handleFoodClick(food)}>
            {food}
          </div>
        ))}
      </div>
      <div className="selected-foods">
        {selectedFoods.map((food, index) => (
          <div key={index}>{food}</div>
        ))}
      </div>
    </div>
  );
}

export default FoodWorldCup;


