import React, { useState } from 'react';

const KeywordRecom = () => {
  const [selectedSex, setSelectedSex] = useState(null);
  const [selectedAge, setSelectedAge] = useState(null);
  const [selectedMenu, setSelectedMenu] = useState(null);

  const handleSexClick = (sex) => {
    if (selectedSex === sex) {
      setSelectedSex(null);
    } else {
      setSelectedSex(sex);
    }
  };

  const handleAgeClick = (age) => {
    if (selectedAge === age) {
      setSelectedAge(null);
    } else {
      setSelectedAge(age);
    }
  };

  const handleMenuClick = (menu) => {
    if (selectedMenu === menu) {
      setSelectedMenu(null);
    } else {
      setSelectedMenu(menu);
    }
  };

  return (
    <div>
      <style> 
      {`
      .keyword_button {
        background-color: red; /* 기본 배경색 (클릭되지 않은 상태) */
        color: white;
        border: none;
        padding: 10px 20px;
        margin: 5px;
        cursor: pointer;
      }

      .keyword_button.active {
        background-color: blue; /* 클릭된 상태의 배경색 */
      }
      `}
      </style>
      <div className="keyword_list_sex">
        <button className={`keyword_button ${selectedSex === '남자' ? 'active' : ''}`}
          onClick={() => handleSexClick('남자')}> 남자 </button>
        <button className={`keyword_button ${selectedSex === '여자' ? 'active' : ''}`}
          onClick={() => handleSexClick('여자')}> 여자 </button>
      </div>
      <div className="keyword_list_age">
        <button className={`keyword_button ${selectedAge === '10대' ? 'active' : ''}`}
          onClick={() => handleAgeClick('10대')}> 10대 </button>
        <button className={`keyword_button ${selectedAge === '20대' ? 'active' : ''}`}
          onClick={() => handleAgeClick('20대')}> 20대 </button>
        <button className={`keyword_button ${selectedAge === '30대' ? 'active' : ''}`}
          onClick={() => handleAgeClick('30대')}> 30대 </button>
        <button className={`keyword_button ${selectedAge === '40대' ? 'active' : ''}`}
          onClick={() => handleAgeClick('40대')}> 40대 </button>
      </div>
      <div className="keyword_list_menu">
        <button className={`keyword_button ${selectedMenu === '한식' ? 'active' : ''}`}
          onClick={() => handleMenuClick('한식')}> 한식 </button>
        <button className={`keyword_button ${selectedMenu === '중식' ? 'active' : ''}`}
          onClick={() => handleMenuClick('중식')}> 중식 </button>
        <button className={`keyword_button ${selectedMenu === '일식' ? 'active' : ''}`}
          onClick={() => handleMenuClick('일식')}> 일식 </button>
        <button className={`keyword_button ${selectedMenu === '양식' ? 'active' : ''}`}
          onClick={() => handleMenuClick('양식')}> 양식 </button>
        <button className={`keyword_button ${selectedMenu === '패스트푸드' ? 'active' : ''}`}
          onClick={() => handleMenuClick('패스트푸드')}> 패스트푸드 </button>
        <button className={`keyword_button ${selectedMenu === '분식' ? 'active' : ''}`}
          onClick={() => handleMenuClick('분식')}> 분식 </button>
      </div>
    </div>
  );
}

export default KeywordRecom;
