import React, { useState } from 'react';

const SignUpPage = (props) => {
  const [id, setId] = useState("");
  const [password, setPassword] = useState("");
  const [password2, setPassword2] = useState("");
  const [startYear] = useState(1980); // 시작 년도 설정
  const [endYear] = useState(new Date().getFullYear()); // 현재 년도 설정

  // 기존에 존재하는 아이디 목록을 가져오는 함수 (임의로 구현)
  const idExists = (userId) => {
    const existingUserIds = ['user01', 'user02', 'user03']; // 기존에 존재하는 아이디 목록
    return existingUserIds.includes(userId); // 아이디가 기존 목록에 포함되어 있는지 확인하고 결과를 반환합니다.
  };

  // 생년월일 선택 시 호출되는 함수
  const handleSelectChange = () => {
    const signUpBirthError = document.getElementById('su_birth_error');
    signUpBirthError.style.display = 'none';
  };

  // 이메일 도메인 선택 시 호출되는 함수
  const handleDomainOptionChange = (event) => {
    const selectedOption = event.target.value;
    const emailDomainTxt = document.getElementById('email_domain_txt');

    if (selectedOption === 'type') {
      emailDomainTxt.value = '';
      emailDomainTxt.disabled = false;
      emailDomainTxt.focus();
    } else {
      emailDomainTxt.value = selectedOption;
      emailDomainTxt.disabled = true;
    }
  };

  // 입력 필드의 keypress 이벤트 처리 함수
  const handleIdInputChange = (event) => {
    const input = event.target.value;
    const regex = /^[a-z0-9]+$/; // 영문 소문자와 숫자만 허용하는 정규식
    const errorMessage = document.getElementById('su_id_error');

    if (!regex.test(input)) {
      event.target.value = input.replace(/[^a-z0-9]/g, '');
      errorMessage.innerText = '영문 소문자와 숫자만 입력할 수 있습니다.';
      errorMessage.style.color = 'red';
      errorMessage.style.display = 'block';
    } else {
      errorMessage.style.display = 'none';
    }
  };

  // 이메일 입력 시 호출되는 함수
  const handleEmailInputChange = (event) => {
    const input = event.target.value;
    const regex = /^[a-zA-Z0-9!@#$%^&*()\-=+_~`{\[\\}|:;"'<,>.?/]*$/; // 영문, 숫자, 특수 기호를 제외한 문자 입력 방지 정규식
    const errorMessage = document.getElementById('su_email_error');

    if (!regex.test(input)) {
      event.target.value = input.replace(/[^a-zA-Z0-9!@#$%^&*()\-=+_~`{\[\\}|:;"'<,>.?/]/g, '');
      errorMessage.innerText = '영문, 숫자, 특수 기호만 입력할 수 있습니다.';
      errorMessage.style.color = 'red';
      errorMessage.style.display = 'block';
    } else {
      errorMessage.style.display = 'none';
    }
  };

  // 전화번호 입력 시 호출되는 함수
  const handleNumberInputChange = (event) => {
    const phoneNumber = event.target.value;
    const formattedNumber = formatPhoneNumber(phoneNumber);
    document.getElementById('user_number').value = formattedNumber;
  };

  // 전화번호 포맷팅 함수
  const formatPhoneNumber = (phoneNumber) => {
    phoneNumber = phoneNumber.replace(/[^0-9]/g, ''); // 입력된 문자열에서 숫자 이외의 문자 제거
    let formattedNumber = '';
    if (phoneNumber.length < 4) {
      formattedNumber = phoneNumber;
    } else if (phoneNumber.length < 7) {
      formattedNumber = phoneNumber.substr(0, 3) + '-' + phoneNumber.substr(3);
    } else if (phoneNumber.length < 11) {
      formattedNumber = phoneNumber.substr(0, 3) + '-' + phoneNumber.substr(3, 3) + '-' + phoneNumber.substr(6);
    } else {
      formattedNumber = phoneNumber.substr(0, 3) + '-' + phoneNumber.substr(3, 4) + '-' + phoneNumber.substr(7, 4);
    }
    return formattedNumber;
  };

  // Caps Lock 상태 확인 함수
  const checkCapsLock = (event, warningId) => {
    const capsLockWarning = document.getElementById(warningId);
    const capsLockEnabled = event.getModifierState && event.getModifierState('CapsLock');

    if (capsLockEnabled) {
      capsLockWarning.innerText = 'Caps Lock이 켜져있습니다.';
      capsLockWarning.style.display = 'block';
    } else {
      capsLockWarning.style.display = 'none';
    }
  };

  return (
    <div className="member">
      <b> 이름 * </b>
      <div className="field_name">
        <input type="text" id="user_name" placeholder="이름 입력" />
      </div>
      <span className="error_message" id="su_name_error"></span>

      <b> 아이디 *</b>
      <div className="field_id">
        <input type="text" id="user_id" placeholder="아이디 입력 (6~20자)" onInput={handleIdInputChange} onChange={event => setId(event.target.value)} />
        {/* <button id="idck_btn" onClick={checkDuplicate}> 중복 확인 </button> */}
      </div>
      <span className="error_message" id="su_id_error"></span>

      <b> 비밀번호  *</b>
      <div className="field_password">
        <input
          className="userpw" type="password" id="user_pw" placeholder="비밀번호 입력 (숫자, 특수문자 포함 8~20자)" onKeyDown={(event) => checkCapsLock(event, 'caps_lock_warning1')} onBlur={() => (document.getElementById('caps_lock_warning1').style.display = 'none')} onChange={event => setPassword(event.target.value)} />
      </div>
      <span className="error_message" id="su_pw_error"></span>
      <span className="error_message" id="caps_lock_warning1"></span>

      <b>비밀번호 확인 *</b>
      <div className="field_password_check">
        <input
          className="userpwck" type="password" id="user_pwck" placeholder="비밀번호 재입력" onKeyDown={(event) => checkCapsLock(event, 'caps_lock_warning2')} onBlur={() => (document.getElementById('caps_lock_warning2').style.display = 'none')} onChange={event => setPassword2(event.target.value)} />
      </div>
      <span className="error_message" id="su_pwck_error"></span>
      <span className="error_message" id="caps_lock_warning2"></span>

      <b>생년월일</b>
      <div className="field_birth">
        <div className="field_birth_year">
          <select id="birth_year" onChange={handleSelectChange}>
            <option disabled selected>출생년도</option>
            {Array.from({ length: endYear - startYear + 1 }, (_, index) => (
              <option key={startYear + index} value={startYear + index}>
                {startYear + index}년
              </option>
            ))}
          </select>
        </div>

        <div className="field_birth_month">
          <select id="birth_month" onChange={handleSelectChange}>
            <option disabled selected>월</option>
            {Array.from({ length: 31 }, (_, index) => (
  <option key={index + 1} value={index + 1}>
    {index + 1}일
  </option>
))}
          </select>
        </div>

        <div className="field_birth_day">
          <select id="birth_day" onChange={handleSelectChange}>
            <option disabled selected>일</option>
            {Array.from({ length: 12 }, (_, index) => (
  <option key={index + 1} value={index + 1}>
    {index + 1}월
  </option>
))}
          </select>
        </div>
        <span className="error_message" id="su_birth_error"></span>
      </div>

      <b>이메일 *</b>
      <div className="field_email">
        <input type="text" id="user_email" placeholder="이메일 주소" onInput={handleEmailInputChange} /> @
        <input className="field_email_domain_txt" id="email_domain_txt" type="text" disabled onInput={handleEmailInputChange} />
        <select className="field_email_domain_option" id="email_domain_option" onChange={handleDomainOptionChange}>
          <option disabled selected>선택</option>
          <option value="type">직접 입력</option>
          <option value="naver.com">naver.com</option>
          <option value="google.com">google.com</option>
          <option value="hanmail.net">hanmail.net</option>
          <option value="nate.com">nate.com</option>
          <option value="kakao.com">kakao.com</option>
          <option value="korea.com">korea.com</option>
        </select>
      </div>
      <span className="error_message" id="su_email_error"></span>

      <b> 전화번호 </b>
      <div className="field_number">
        <div>
          <input type="text" name="phone" id="user_number" placeholder="휴대폰 번호 입력" onInput={handleNumberInputChange} />
        </div>
      </div>
      <span className="error_message" id="su_number_error"></span>

      <input type="submit" id="signup_button" value="가입" onClick={() => {
        const userData = {
          userId: id,
          userPassword: password,
          userPassword2: password2,
        };
        fetch("http://localhost:3001/signup", {
          method: "post",
          headers: {
            "content-type": "application/json",
          },
          body: JSON.stringify(userData),
        })
          .then((res) => res.json())
          .then((json) => {
            if (json.isSuccess === true) {
              alert('회원가입이 완료되었습니다!');
            } else {
              alert('회원가입이 실패했습니다. 이유: ' + json.errorMessage);
            }
          })
          .catch((error) => {
            console.error("Fetch 오류:", error);
          });
      }} />

      <input type="button" id="signup_cancel_button" value="취소" />
    </div>
  );
};

export default SignUpPage;
