import React, { useState, useEffect } from 'react';

const MapFoodType = () => {
  const [selectedRestaurant, setSelectedRestaurant] = useState(null);
  const [map, setMap] = useState(null);
  const [markers, setMarkers] = useState([]);
  const [selectedFoodType, setSelectedFoodType] = useState([]);

  useEffect(() => {
    const script = document.createElement('script');
    script.src =
      'https://dapi.kakao.com/v2/maps/sdk.js?appkey=0560729fbda53d06035a69b6f812c484&libraries=services';
    script.async = true;
    script.onload = () => {

      const mapOptions = {
        center: new window.kakao.maps.LatLng(36.773536, 126.933500),
        level: 2,
      };

      const mapInstance = new window.kakao.maps.Map(
        document.getElementById('map'),
        mapOptions
      );
      setMap(mapInstance);

      const positions = [
        {
          title: '계경',
          latlng: new window.kakao.maps.LatLng(36.775725, 126.933723),
          type: ['한식'],
          number: '041-541-1444',
          menu: '부대찌개, 콩불',
        },
