import React from 'react';
import { useLocation } from 'react-router-dom';

const MyInfoHeart = () => {
  const location = useLocation();
  const { restaurantInfo, isFavorite } = location.state;

  // 이제 이 컴포넌트 안에서 받은 정보를 활용할 수 있어요.

  return (
    <div>
      {/* 받은 정보를 이용해서 원하는 작업을 할 수 있어요 */}
      <h2>음식점 이름: {restaurantInfo.title}</h2>
      <p>찜한 상태: {isFavorite ? '찜한 상태' : '찜하지 않은 상태'}</p>
      {/* 받은 정보를 이용한 다른 작업 */}
    </div>
  );
};

export default MyInfoHeart;
