import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';

function PostDetail({ posts }) {
  const { id } = useParams();

  // posts 배열이 정의되지 않았거나 빈 배열인 경우를 고려하여 초기화
  const post = posts && posts.length > 0 ? posts.find((post) => post.id === parseInt(id)) : null;

  if (!post) {
    return <div>게시물을 찾을 수 없습니다.</div>;
  }

  return (
    <div>
      <h1>{post.title}</h1>
      <p>작성일: {post.date}</p>
      <p>조회수: {post.views}</p>
      <p>{post.content}</p>
    </div>
  );
}

export default PostDetail;
