import React, { useState, useEffect } from 'react';

const options = ["치킨", "피자", "햄버거", "삼겹살", "족발보쌈", "국수", "초밥", "마라탕"];

function FoodWorldCup() {
  const initialFoods = ['Pizza', 'Burger', 'Sushi', 'Taco', 'Pasta'];

  const [tournament, setTournament] = useState([{ foods: initialFoods }]);

  const handleSelectWinner = (winner) => {
    const currentRound = tournament[tournament.length - 1].foods;
    if (currentRound.length === 1) {
      alert(`The winner is ${winner}!`);
    } else {
      const nextRound = [];
      for (let i = 0; i < currentRound.length; i += 2) {
        nextRound.push(winner);
      }
      setTournament([...tournament, { foods: nextRound }]);
    }
  };

  return (
    <div className="App">
      <h1>Food Tournament</h1>
      {tournament.map((round, index) => (
        <div key={index}>
          <h2>Round {index + 1}</h2>
          {round.foods.map((food, foodIndex) => (
            <div key={foodIndex} className="match">
              {food}
              <button onClick={() => handleSelectWinner(food)}>Select</button>
            </div>
          ))}
        </div>
      ))}
    </div>
  );
}

export default FoodWorldCup;


